INSERT INTO `sanpham` (`idsp`, `danhmuc`, `hinhanh`, `tensp`, `giasp`, `giacu`, `loai`, `MoTa`) VALUES
(1, 'kitchen', '../images/maysaysinhto.png', 'Máy xay sinh tố', 500000, 0, '', 'Máy xay sinh tố là một thiết bị gia dụng phổ biến dùng để xay, nghiền hoặc trộn các loại thực phẩm như trái cây, rau củ, đá viên, và hạt để tạo ra sinh tố, nước ép, hoặc các món ăn dạng sệt.\r\n Máy xay sinh tố có cấu tạo gồm một thân máy với động cơ điện, cối xay có lưỡi dao sắc bén và nắp đậy, và có các nút điều khiển tốc độ hoặc chế độ xay khác nhau. Khi bật máy, động cơ sẽ quay lưỡi dao bên trong cối với tốc độ cao, giúp nghiền nhỏ và trộn đều nguyên liệu.\r\n\r\nCác thành phần chính:\r\n-Thân máy: Chứa động cơ và bảng điều khiển.\r\n-Cối xay: Được làm từ thủy tinh hoặc nhựa, có thể tháo rời để dễ vệ sinh.\r\n-Lưỡi dao: Thường làm từ thép không gỉ, có thể tháo ra để dễ dàng làm sạch.\r\n-Nắp đậy: Có thể kèm theo lỗ cho thêm nguyên liệu khi đang xay.\r\n\r\nCông dụng:\r\n-Làm sinh tố: Xay nhuyễn trái cây và rau để tạo ra sinh tố.\r\n-Nghiền đá: Để làm các món uống lạnh.\r\n-Xay hạt, rau củ: Tạo ra các hỗn hợp cho nhiều loại món ăn.\r\nMáy xay sinh tố tiện dụng trong các bếp gia đình và quán nước nhờ sự đa năng, giúp chế biến món uống và thực phẩm nhanh chóng và tiện lợi.'),
(2, 'cleaning', '../images/mayhutbuithongminh.png', 'Máy hút bụi thông minh', 3200000, 3400000, 'hangbanchay', 'Máy hút bụi thông minh là thiết bị gia dụng tự động, có khả năng di chuyển và làm sạch sàn nhà mà không cần sự can thiệp của người dùng. Những máy này được trang bị công nghệ tiên tiến như cảm biến, camera, và hệ thống lập bản đồ để tự động nhận diện vị trí, tránh vật cản, lên kế hoạch di chuyển tối ưu và thực hiện việc hút bụi trên toàn bộ bề mặt sàn.\r\n\r\nĐặc điểm nổi bật:\r\nCảm biến thông minh: Nhận diện vật cản và tạo đường đi tối ưu để không va chạm vào đồ vật hoặc tường.\r\nHệ thống lập bản đồ: Một số dòng máy cao cấp có thể vẽ bản đồ ngôi nhà và lên kế hoạch di chuyển để không bỏ sót bất kỳ khu vực nào.\r\nKết nối với ứng dụng di động: Người dùng có thể điều khiển máy từ xa, lên lịch làm việc hoặc xem bản đồ dọn dẹp từ điện thoại.\r\nTự động quay về trạm sạc: Khi pin yếu, máy sẽ tự động quay về trạm sạc và sau khi sạc đầy, nó có thể tiếp tục công việc nếu còn khu vực chưa được dọn dẹp.\r\nChế độ làm sạch đa dạng: Tùy theo dòng máy, có thể cài đặt chế độ làm sạch khác nhau, như hút bụi nhẹ nhàng hoặc hút mạnh cho các khu vực nhiều bụi.\r\n\r\nCông dụng:\r\nLàm sạch tự động: Phù hợp cho sàn nhà, gạch men, thảm mỏng và khu vực khó tiếp cận.\r\nTiết kiệm thời gian: Máy tự động làm sạch mà không cần sự can thiệp của người dùng, rất tiện lợi cho người bận rộn.\r\nĐiều khiển từ xa: Có thể điều khiển qua ứng dụng di động, giúp dễ dàng lên lịch và quản lý hoạt động của máy.\r\nMáy hút bụi thông minh là một lựa chọn lý tưởng để giữ cho ngôi nhà luôn sạch sẽ mà không mất nhiều công sức, đặc biệt với những gia đình hiện đại, bận rộn.\r\n\r\n\r\n\r\n\r\n\r\n\r\n'),
(3, 'climate', '../images/maylanh.jfif', 'Máy lạnh', 12000000, 13000000, 'hangbanchay', '**Đặc điểm nổi bật\r\n\r\n- Điều hòa LG công suất 1.5HP thích hợp cho không gian phòng từ 15 - 20m²\r\n\r\n- Máy nén biến tần Inverter làm lạnh nhanh, tiết kiệm điện, vận hành êm\r\n\r\n- Màng lọc bụi loại bỏ đến 90% bụi mịn PM2.5, bảo vệ sức khỏe cả nhà\r\n\r\n- Tích hợp công nghệ Wifi, điều khiển từ xa bằng điện thoại thông minh\r\n\r\n- Công nghệ Gold-Fin chống ăn mòn giúp thiết bị bền bỉ với thời gian\r\n\r\n- Tự khởi động lại khi có điện, tiết kiệm thời gian thiết lập lại máy\r\n\r\n\r\n\r\n*THÔNG TIN CHUNG\r\n\r\n- Model: V13WIN1\r\n\r\n- Màu sắc: Trắng\r\n\r\n- Nhà sản xuất: LG\r\n\r\n- Loại máy lạnh: Máy lạnh 1 chiều (chỉ làm lạnh)\r\n\r\n- Kiểu dáng: Máy lạnh treo tường\r\n\r\n- Công suất: 1.5 HP\r\n\r\n- Kích thước dàn lạnh (RxSxC): 837 x 308 x 189 mm\r\n\r\n- Kích thước dàn nóng (RxSxC): 717 x 495 x 230 mm\r\n\r\n- Khối lượng dàn lạnh: 8.2 kg\r\n\r\n- Khối lượng dàn nóng: 21.4 kg'),
(4, 'other', '../images/denpin.png', 'Đèn pin', 150000, 0, '', '✔ Đèn Pin Siêu Sáng Cầm Tay Pin To 18650 HÀNG LOẠI 1 sử dụng bóng LED chip XHP50 30W nhập khẩu MỸ cho độ sáng chói cực mạnh với khả năng rọi xa tới 500m và cực kì bền bỉ lên đến 100000 giờ\r\n\r\n\r\n\r\nĐèn Pin Siêu Sáng Cầm Tay Pin To 18650 là sản phẩm tiện ích cho mỗi gia đình, phù hợp với các bạn trẻ thích dã ngoại cắm trại, đi bắn chim, câu cá đêm, đi soi đêm hay lái xe ban đêm ...\r\n\r\n✔ Sản phẩm có tích hợp pin sạc tích điện chính hãng dung lượng lớn 1200mAh để bạn lựa chọn hoặc có thể dùng pin tiểu AAA.\r\n\r\nCHI TIẾT SẢN PHẨM: Đèn Pin Siêu Sáng Cầm Tay Pin To 18650 \r\n\r\n- Sản phẩm: Đèn Pin Siêu Sáng Cầm Tay Pin To 18650 \r\n\r\n- Cổng sạc: Sạc micro USB\r\n\r\n- Công suất đèn: 30W\r\n\r\n- Điện áp: 3,7V\r\n\r\n- PIN 18650: thời gian sử dụng khoảng 3h\r\n\r\n- Thời gian sạc: 6h\r\n\r\n- Bóng đèn: Bóng LED chip XHP50 nhập khẩu Mỹ cho tuổi thọ 100.000 giờ\r\n\r\n- Màu sắc đèn: Ánh sáng trắng rõ ràng 6500K\r\n\r\n- Độ sáng: 1800 lumens (lumens thật)\r\n\r\n- Chế độ sáng: 5 chế độ 100% -70% -50% -Flash-SOS\r\n\r\n- Sử dụng pin: pin 1x18650 \r\n\r\n- Chế độ thu phóng: Thu phóng lấy nét\r\n\r\n- Chất liệu đèn pin: Hợp kim nhôm sắc nét\r\n\r\n- Chống nước đi mưa\r\n\r\n- Phạm vi sáng tối đa: 500 mét\r\n\r\n- Kích thước sản phẩm: 16,6x4x3,3cm\r\n\r\n- Trọng lượng sản phẩm: 195g\r\n\r\n- Đèn có chế độ báo lượng pin còn lại tại công tắc 100% - 75% - 50% - 25%'),
(5, 'furniture', '../images/sofa.jpg', 'Sofa', 3500000, 0, '', 'Ghế Sofa đa năng chân kim loại giá cực tốt ghế dài 1m7 rộng 95cm\r\n\r\n\r\n\r\nThông tin sản phẩm:\r\n\r\nKích thước: 1.7m x 95cm\r\n\r\nCó chất liệu simili, vải bố và vải nhung\r\n\r\nGhế thanh lý giá siêu rẻ thu hồi vốn\r\n\r\nHình ảnh chụp thực tế bằng điện thoại tại kho\r\n\r\nGhế có chân kim loại mạ Crom \r\n\r\nHàng mới 100%\r\n\r\nHàng thanh lý tại kho chưa bao gồm phí vận chuyển\r\n\r\nSản phẩm được sản xuất theo quy trình xuất khẩu bên trong khung có lò xo và dây thun nên rất êm. Bên trong ghế với hệ lò xo (8 cây lò xo cuộn) và dây thun tạo độ phồng giúp sản phẩm thêm bắt mắt và đẹp hơn.\r\n\r\n\r\n\r\nHướng dẫn sử dụng:\r\n\r\n1. Mở thùng ghế ra. Mặt sau màu đen quý khách sẽ nhìn thấy 1 sợi dây kéo, bên trong là hộp chân ghế. Lấy chân ghế vặn vào theo hình ghế sau đó dùng lục giác siết ốc chân ghế cho thật chặt.\r\n\r\n2. Để bật thành ghế quý khách đẩy nhẹ thành tựa lung về phía trước ghế sẽ tự đứng (không nên đẩy quá mạnh sẽ qua khớp làm ghế không đứng được).\r\n\r\n3. Để hạ thành giường chỉ cần đẩy nhẹ lưng ghế về phía trước\r\n\r\nLưu ý: Để đảm bảo độ bền và ổn định cảu sản phẩm không nên nhún nhảy trên ghế. Sử dụng nhẹ nhàng không ngồi mạnh hay thả người rơi tự do trên ghế. Khi ngủ nên trải thêm một lớp ở trên mặt ghế. Đặt ghế ở vị trí phù hợp, bằng phẳng tránh bị chênh vênh. Khi vệ sinh sản phẩm, nên sử dụng khăn mềm thấm nước để lau chùi rồi lập tức sấy khô.'),
(6, 'kitchen', '../images/noicom.png', 'Nồi cơm điện', 800000, 1000000, 'hangbanchay', 'Đặc điểm nồi bật \r\n\r\n1, Dung tích: 1.8L, phù hợp cho việc nấu cơm cho gia đình đông người  từ 4-6 thành viên.\r\n\r\n2, Lòng nồi nhôm phủ sơn Ceramic siêu an toàn và siêu bền, không giải phóng chất PFOA gây ung thư :  Sử dụng vật liệu nhôm bền và dẫn nhiệt tốt, được phủ lớp chống dính Ceramic giúp dễ dàng làm sạch sau khi sử dụng và giảm nguy cơ bám đính thức ăn.\r\n\r\n3, Lòng nồi siêu dày : 2.2mm đủ dày giúp nhiệt độ được truyền đều khắp bề mặt cơm khiến cơm chín đều hơn, ngon hơn .\r\n\r\n4, Vỏ ngoài bằng nhựa: Vỏ ngoài được làm từ chất liệu nhựa chất lượng cao, làm nhiệt độ vỏ không bị quá nóng, đảm bảo an toàn khi sử dụng và dễ dàng lau chùi.\r\n\r\n5, Chức năng ủ 3D: Thiết kế với chế độ ủ trong một ngày giúp thức ăn được giữ ấm lâu hơn mà không ảnh hưởng đến chất lượng cơm sau khi ủ thời gian dài.\r\n\r\n6, Thiết kế đơn giản hiện đại, dễ sử dụng cho mọi lứa tuổi \r\n\r\n7, Đặc biệt : Nồi được thiết kế nấu siêu nhanh tiết kiệm thời gian nấu, hỗ trợ cho các chị em bận rộn nhưng vẫn đảm bảo cơm ngon tuyệt đối.\r\n\r\n\r\nThông số kỹ thuật \r\n\r\n\r\nThông tin sản phẩm \r\n\r\nTên sản phẩm : Nồi cơm điện Elmich 1.8L RCE-9128\r\n\r\nMã sản phẩm : 4029128\r\n\r\nNhàn hiệu : Elmich \r\n\r\nCông suất : 700W\r\n\r\nĐiện áp : 220VAC\r\n\r\nDung tích : 1,8lit\r\n\r\nChất liệu lòng nồi : Lòng nồi dày 2.2mm làm bằng nhôm phủ sơn Ceramic an toàn tuyệt đối cho sức khỏe.\r\n\r\nPhím điều khiển : Phím cơ \r\n\r\nkích thước sản phẩm : 280x330x250 mm\r\n\r\nTrọng lượng sản phẩm : 3280g'),
(7, 'kitchen', '../images/lovisong.png', 'Lò vi sóng', 2500000, 0, '', 'Thông số kỹ thuật:\r\n\r\nLoại sản phẩm: Lò vi sóng\r\n\r\nLoại lắp đặt: Đứng độc lập\r\n\r\nLoại năng lượng: Điện\r\n\r\nTần số (Hz): 50\r\n\r\nNguồn điện (V): 220-240\r\n\r\nPhụ kiện đi kèm: Khay kính, cụm vòng xoay, trục bàn xoay\r\n\r\nMàu sắc: Trắng\r\n\r\nChất liệu khoang lò: Kim loại\r\n\r\nDạng điều khiển: Điều khiển cơ\r\n\r\nTính năng:\r\nChương trình cài đặt sẵn trực quan dễ sử dụng giúp nấu ăn nhanh chóng.\r\nRã đông thực phẩm nhanh chóng và đều.\r\n5 mức công suất giúp nấu nướng linh hoạt.\r\nMô tả:\r\nChương trình cài sẵn tiện lợi\r\n\r\nThực đơn cài đặt sẵn dễ sử dụng, trực quan với thời gian và công suất nấu phù hợp với nhiều phương pháp nấu và loại thực phẩm.\r\n\r\nRã đông nhanh chóng\r\n\r\nRã đông thực phẩm đều và nhanh chóng với chức năng rã đông tự động của lò vi sóng. Chức năng này cung cấp lượng điện năng và thời gian cần thiết để rã thực phẩm đông lạnh nhanh, sẵn sàng cho bạn chế biến.\r\n\r\nĐiều chỉnh đa dạng mức công suất\r\n\r\n5 mức công suất giúp bạn linh hoạt trong việc điều chỉnh công suất vi sóng theo nhu cầu\r\nHâm nóng nhanh hơn nhờ công suất lớn\r\nCông suất 800W giúp hâm nóng thức ăn nhanh hơn.\r\n\r\n\r\n\r\n\r\n\r\n'),
(8, 'kitchen', '../images/mayphacaphe.png', 'Máy pha cà phê', 1500000, 0, '', 'Máy pha cà phê Espresso bán tự động phong cách Vintage Zamboo ZB-901PRO -2023 màu xanh lá ( Green)\r\n\r\nThông số kĩ thuật\r\n\r\n-Công suất: 1350w \r\n\r\n-Điện áp     :220v-50Hz\r\n\r\n-Áp suất tối đa 20bar\r\n\r\n-Áp suất pha Espresso 8-10 bar\r\n\r\n-Bình chứa nước 1,8 lit\r\n\r\n-Đồng hồ hiển thị thời gian và nhiệt độ nước.\r\n\r\n-2 chế độ pha tự ngắt 1 cup /2 cup\r\n\r\n-Vòi hơi đánh sữa+ lấy nước nóng\r\n\r\n -Bộ tay phin gồm 1 filter 1 cup và 2 cup ,thiết kế chuyên nhiệp với 2 vòi chảy bằng inox chắc chắn\r\n\r\n  + Filter 1 cup chứa 6-7gram cà phê \r\n\r\n   +Filter 2 cup chứa 12-14gram cà phê\r\n\r\n- Khay hứng nước tháo rời vệ sinh dễ dàng \r\n\r\n-Model ZB-901PRO là dòng máy pha cà phê dành cho gia đình hoặc văn phòng nhỏ của thương hiệu Zamboo  sản xuất năm 2023\r\n\r\n-Công suất của máy Zamboo ZB-901PRO  1 ngày có thể pha từ 15-20ly một cách nhanh chóng \r\n\r\n-Máy có thiết kế rất ấn tượng mang hơi hướng vừa hiện đại vừa cổ điển, kiểu dáng hiện đại ,phối màu crom  và xanh lá  mang tới màu sắc tươi sáng , cho bạn thêm ý tưởng decor cho căn bếp xinh của mình.\r\n\r\nMáy pha cà phê Zamboo ZB90-PRO dùng pha chế được các loại cà phê như\r\n\r\n1: Espresso \r\n\r\n2:Capuchino \r\n\r\n3: Latte \r\n\r\n4: Americano (1 phần Espresso và 6 phần nước nóng 70độ)\r\n\r\n5: Longblack (1 phần Espresoo và 3 phần nước nóng 70 độ)\r\n\r\n6: Đánh sữa nóng hoặc lấy nước nóng..\r\n\r\nCó thể dùng cho quán Takeawway pha ngày tầm 20-30ly\r\n\r\nMua máy bạn sẽ được hướng dẫn sử dụng tận tình và công thức pha chế các loại thức uống từ cafe '),
(9, 'kitchen', '../images/beptu.png', 'Bếp từ', 3000000, 3500000, 'hangbanchay', '- Tên Sản phẩm: Bếp hồng ngoại cơ Sunhouse SHD6011 2200W\r\n\r\n- Thương hiệu: Sunhouse\r\n\r\n- Model: SHD6011\r\n\r\n- Loại bếp: bếp dương\r\n\r\n- Công suất: 2200 W\r\n\r\n- Điện áp: 220 - 240V\r\n\r\n- Tần số: 50~60Hz\r\n\r\n- Nhiệt độ: 60 - 240°C\r\n\r\n- Hẹn giờ: có\r\n\r\n- Cảnh báo âm thanh: có\r\n\r\n- Mặt bếp: kính cường lực siêu bền, chịu nhiệt đến 600 độ C\r\n\r\n- Bảng điều khiển: cơ\r\n\r\n- Kích thước: 36x29x6 (cm)\r\n\r\n- Trọng lượng: 2 kg\r\n\r\n- Xuất xứ: Trung Quốc\r\n\r\n- Bảo hành: 12 tháng\r\n\r\n\r\n\r\n  *Đặc điểm nổi bật:\r\n\r\n\r\n\r\nThiết kế nhỏ gọn, tinh tế\r\n\r\nBếp có thiết kế màu đen mạnh mẽ, kiểu dáng nhỏ gọn, dễ dàng bố trí trên kệ bếp, bàn ăn hoặc di chuyển khi cần. \r\n\r\n\r\n\r\nChất liệu cao cấp\r\n\r\n- Bếp hồng ngoại cơ Sunhouse SHD6011 2200W có mặt bếp làm bằng kính cường lực siêu bền, chống xước, chịu được nhiệt độ lên tới 600 độ C, tránh cho bề mặt bếp bị biến dạng hoặc nóng chảy. Chất liệu kính sáng bóng, hạn chế trầy xước, dễ dàng làm sạch.\r\n\r\n- Vỏ bếp bằng nhựa cao cấp siêu bền, chịu nhiệt tốt.\r\n\r\n\r\n\r\nCông nghệ đốt nhiệt hiện đại\r\n\r\nBếp sử dụng mâm nhiệt lớn với nhiều vòng phát nhiệt giúp truyền nhiệt tốt và đều hơn, việc nấu nướng được trở nên nhanh chóng.\r\n\r\n\r\n\r\nBảng điều khiển đa chức năng, thân thiện người dùng\r\n\r\n- Bếp hồng ngoại cơ Sunhouse SHD6011 2200W có bảng điều khiển cơ nhạy bén trang bị nhiều chức năng: hẹn giờ, tự động tắt để bảo vệ bếp và cả chức năng cảnh báo bằng âm thanh giúp bạn dễ dàng theo dõi quá trình hoạt động của bếp.\r\n\r\n- Cài đặt nhiệt độ với 5 chế độ nấu khác nhau: Đun nước, Súp, Nướng, Rán, Hâm nóng. Đặc biệt, chức năng nướng trực tiếp mang đến sự tiện lợi tối đa khi có thể làm chín mọi thực phẩm như thịt, cá, hải sản…\r\n\r\n- Chức năng hẹn giờ bật trước 12 giờ và cài đặt tắt trước 4 giờ giúp người dùng tiết kiệm được thời gian trông coi.\r\n\r\n\r\n\r\nCông suất hoạt động mạnh\r\n\r\n- Sản phẩm trang bị mâm nhiệt sử dụng mâm từ bằng đồng nguyên chất với công suất 2200W giúp công việc nấu nướng trở nên nhanh chóng, tiết kiệm thời gian và điện năng tiêu thụ.\r\n\r\n- Dưới mặt bếp được thiết kế cánh quạt tản nhiệt giúp mặt kính nhanh nguội sau khi sử dụng.'),
(10, 'kitchen', '../images/maynuongbanh.png', 'Máy nướng bánh', 700000, 0, '', 'I, THÔNG SỐ KĨ THUẬT:\r\n- Tên sản phẩm: Máy nướng bánh mì\r\n- Kích thước sản phẩm: 25 * 13 * 17cm \r\n- Màu sản phẩm: Bạc Đen\r\n- Máng bánh mì: 2 miếng\r\n- Thiết bị định thời: 5 tệp\r\n- Công suất nướng bánh mỳ: 800W\r\n\r\nII, ĐẶC ĐIỂM ƯU VIỆT CỦA SẢN PHẨM:\r\n- Lò nướng bánh Sandwich là một thiết bị chuyên dụng giúp bạn nướng bánh mì giòn thơm để có bữa sáng nhanh chóng cùng gia đình. Lò nướng bánh Sandwich được thiết kế đặc biệt với chế độ nướng với chức năng rã đông đáp ứng tối đa nhu cầu sử dụng của người dùng.\r\n\r\n- Hoạt động với công suất mạnh mẽ:\r\n+ Lò nướng bánh Sandwich hoạt động mạnh mẽ với công suất 800W, giúp nướng bánh mì nhanh giòn, thơm ngon, bạn sẽ có bữa ăn sáng nhanh chóng để bắt đầu một ngày làm việc mới.\r\n\r\n- Nhiều chế độ nướng:\r\n+ Máy nướng bánh Sandwich chính hãng có chế độ nướng cùng chức năng rã đông và làm nóng tiện lợi, đáp ứng nhiều nhu cầu sử dụng của bạn. Giờ đây, bạn không còn lo mỗi bữa sáng phải mất nhiều thời gian hoặc công sức cho việc chế biến bữa sáng, chỉ với chiếc lò nướng này, bạn có thể tha hồ sáng tạo nhiều nhân bánh khác nhau cho cả nhà, vừa đáp ứng nhu cầu dinh dưỡng, vừa nhanh chóng và tiện lợi.\r\n\r\n- Dễ dàng sử dụng:\r\n+ Lò nướng có kết cấu đơn giản, các chức năng của sản phẩm được hiển thị đầy đủ, rõ ràng, bạn có thể sử dụng dễ dàng mà không gặp bất cứ khó khăn nào. Bạn chỉ việc thao tác đơn giản với các nút điều khiển cơ và núm xoay. Thiết kế hiện đại, sang trọng.\r\n+ Sản phẩm được thiết kế với kiểu dáng hiện đại, cùng màu trắng sang trọng, sẽ tô điểm cho gian bếp nhà bạn thêm phần xinh đẹp.\r\n\r\n- Thiết kế tiện dụng:\r\n+ Lò nướng trang bị khay đựng bánh mì vụn, giữ cho gian bếp luôn sạch sẽ trong quá trình nướng bánh. Ngoài ra, thiết kế khay nướng có thể tháo rời, cùng bề mặt sản phẩm trơn láng, thuận tiện trong việc vệ sinh máy.\r\n'),
(11, 'kitchen', '../images/mayruachen.png', 'Máy rửa chén', 5000000, 5500000, 'hangbanchay', 'Tổng quan thiết kế\r\n\r\n- Máy rửa chén độc lập Hafele HDW-F605B (535.29.660) có kiểu thiết kế điện đại với gam màu đen sang trọng, dễ dàng phối hợp với nhiều nội thất khác bên trong phòng bếp.\r\n\r\n- Phần cửa và vỏ máy đều làm bằng thép không gỉ, có khả năng giảm thiểu trầy xước và chống biến dạng theo thời gian.\r\n\r\n- Nhờ thuộc kiểu máy rửa chén độc lập, bạn có thể lắp đặt sản phẩm tại mọi vị trí mà vẫn đảm bảo khả năng hoạt động của máy rửa chén và tính thẩm mỹ trong khu vực nấu nướng của gia đình bạn.\r\n\r\nKhay rửa + giá đỡ\r\n\r\nMáy rửa chén Hafele này được thiết kế bên trong gồm có 3 khay rửa: khay dao kéo, khay trên và khay dưới đều có thể kéo ra vào linh hoạt, nhờ đó thuận tiện cho bạn mỗi khi thao tác.\r\n\r\nCông suất\r\n\r\n- Máy rửa chén có công suất hoạt động từ 1760W - 2100W, có thể rửa được số lượng chén bát dùng cho 3 - 4 bữa ăn Việt (khoảng gần 15 bộ đồ ăn châu Âu) trong mỗi lần rửa, góp phần tiết kiệm thời gian và công sức rửa chén bát cho bạn sau khi dùng bữa với gia đình.\r\n\r\n- Cứ mỗi lần rửa, máy tiêu thụ khoảng 9.8 lít nước.\r\n\r\n- Độ ồn khi máy rửa chén độc lập Hafele vận hành ở mức 47 dB, gần với độ ồn của tiếng mưa rơi vừa phải.\r\n\r\nChương trình rửa\r\n\r\nSản phẩm được thiết kế 8 chương trình rửa, thuận tiện cho bạn lựa chọn như:\r\n\r\n+ Tự làm sạch.\r\n\r\n+ Rửa nhanh: Sử dụng nhiệt độ nước 40°C.\r\n\r\n+ Rửa tiết kiệm: Sử dụng nhiệt độ nước 50°C.\r\n\r\n+ Rửa tự động: Sử dụng nhiệt độ nước từ 30 - 50°C với thời gian rửa khoảng 79 - 87 phút.\r\n\r\n+ Rửa ly tách dễ vỡ: Sử dụng nhiệt độ từ 30 - 50°C.\r\n\r\n+ Rửa 90 phút.\r\n\r\n+ Rửa ngâm.\r\n\r\n+ Rửa vệ sinh.\r\n\r\nCông nghệ rửa - sấy\r\n\r\n- Công nghệ phun nước - rửa bằng nước nóng: Sử dụng nước nóng có nhiệt độ đến 72°C, giúp loại bỏ các vết gia vị bị cháy xém trên nồi chảo hiệu quả, đồng thời loại bỏ vết dầu mỡ và mùi hôi đáng kể bám trên chén bát, từ đó nâng cao hiệu quả rửa.\r\n\r\n- Công nghệ Purebeam - diệt khuẩn khử mùi bằng tia UV: Sử dụng tia UV có khả năng ức chế sự phát triển của vi khuẩn và nấm mốc, nhờ đó giúp cho chén bát cũng như các dụng cụ ăn uống và nấu nướng trở nên sạch sẽ, an toàn cho sức khỏe người dùng trong suốt 72 tiếng sau khi rửa\r\n\r\n- Tăng tốc độ rửa: Khi kích hoạt chức năng này, máy rửa chén sẽ tăng nhiệt độ rửa và áp lực nước, để đẩy nhanh quá trình rửa chén bát, tiết kiệm thời gian chờ đợi mà vẫn đảm bảo hiệu quả làm sạch.\r\n\r\n- Sấy tăng cường: Có thể tăng nhiệt độ đến 70°C ở chu trình xả cuối cùng, giúp chén bát được làm khô nhanh chóng, đồng thời góp phần khả năng diệt khuẩn và khử mùi hôi chén bát hiệu quả.\r\n\r\nBảng điều khiển\r\n\r\nBảng điều khiển được thiết kế dạng nút cảm ứng với ký hiệu đơn giản, giúp bạn dễ nhận biết và sử dụng các chức năng của máy rửa chén. Hơn nữa, màn hình hiển thị được bố trí ở giữa bảng điều khiển, hỗ trợ khả năng quan sát chức năng và thời gian rửa được thuận tiện mỗi khi bạn thao tác.\r\n\r\n\r\n\r\nTiện ích\r\n\r\n- Rửa bán tải Half load: Có thể rửa chén bát ở số lượng ít (không đủ tải) với việc sử dụng tiết kiệm điện và nước tối ưu mà vẫn đảm bảo khả năng làm sạch chén bát sau khi rửa.\r\n\r\n- Khoá trẻ em: Ngăn chặn trẻ nhỏ nghịch phá mở cửa máy rửa chén khi đang hoạt động, nhờ đó đảm bảo an toàn cho trẻ và quá trình rửa chén diễn ra trơn tru hơn.\r\n\r\n- Hẹn giờ bắt đầu rửa: Cho phép bạn cài đặt giờ hoạt động của máy rửa chén từ 1 - 24 tiếng, thuận lợi cho việc sắp xếp công việc của bạn, cũng như đảm bảo việc dùng bữa của gia đình không bị gián đoạn.\r\n\r\n- Aquastop - tự ngắt nguồn nước khi có rò rỉ: Phát hiện và ngăn chặn tình trạng rò rỉ nước khi xảy ra, nhờ đó đảm bảo máy vận hành ổn định và an toàn cho gia đình bạn.\r\n\r\nNhìn chung, máy rửa chén độc lập Hafele HDW-F605B (535.29.660) có kiểu dáng sang trọng, dễ lắp đặt và dễ bố trí trong khu vực nhà bếp. Sản phẩm có thể loại bỏ vết dầu mỡ và vết thức ăn cứng đầu, đồng thời hỗ trợ diệt khuẩn và mùi hôi bám trên các dụng cụ ăn uống và nấu nướng của gia đình hiệu quả, nhờ đó góp phần bảo vệ sức khỏe tốt hơn cho cả gia đình bạn.\r\n\r\n'),
(12, 'kitchen', '../images/noiapsuatdien.png', 'Nồi áp suất điện', 1000000, 0, '', 'Nồi áp suất cao cấp đa chức năng Elmich PCS-1805 dung tích 2,5L\r\n\r\nThương hiệu:Elmich\r\n\r\nMã sản phẩm:4021805\r\n\r\nMàu : Đen nâu\r\n\r\nXuất xứ:Trung Quốc\r\n\r\nTình trạng:Hàng sẵn kho \r\n\r\nBảo hành :24 Tháng ( 1 đổi 1 trong 12 tháng )\r\n\r\nThông số kỹ thuật\r\n\r\nThể tích lòng nồi : 2,5lit ( tương đương nồi cơm 1lit )\r\n\r\nĐiện áp: 220V～50Hz\r\n\r\nCông suất: 650W\r\n\r\nChất liệu lòng nồi: Nhôm nguyên chất phủ chống dính cao cấp Whitford (Mỹ)\r\n\r\nChức năng: Nấu cơm, nấu súp, nấu cháo, nấu đậu, làm bánh, hầm gân, hấp cá,…\r\n\r\nĐặc tính sản phẩm\r\n\r\n1. Đa chức năng nấu, dễ dàng điều khiển\r\n\r\nNồi áp suất Elmich PCE-1805 rất dễ sử dụng với hệ thống điều khiển cơ\r\n\r\nTiện lợi với chương trình nấu đã được cài đặt sẵn, đa chức năng: nấu cơm, nấu súp, nấu cháo, nấu đậu, làm bánh, hầm gân, hấp cá… giúp tiết kiệm thời gian đun nấu mà vẫn chế biến được nhiều món ăn ngon cho cả gia đình.\r\n\r\n\r\n2. Chất liệu lòng nồi tuyệt đối an toàn cho sức khỏe\r\n\r\nLòng nồi dày 2mm được làm từ nhôm nguyên chất cho khả năng giữ nhiệt lâu, bên ngoài phủ chống dính cao cấp Whitford (Mỹ) an toàn tuyệt đối cho sức khỏe.\r\n\r\n\r\n3. Thiết kế thông minh, dễ dàng vệ sinh\r\n\r\nThể tích 2,5L tiện dụng cho nhiều loại món ăn, cấu tạo lòng nồi dày 2mm giúp khả năng giữ nhiệt lâu.\r\n\r\nViệc vệ sinh nồi vô cùng đơn giản bởi các bộ phận có thể tháo rời và chất liệu rất dễ làm sạch.'),
(13, 'kitchen', '../images/mayxaythit.png', 'Máy xay thịt', 850000, 900000, 'hangmoi', 'Máy xay thịt 2L công suất 300W - EJM171\r\n\r\nMàu sắc: Bạc\r\n\r\nDung tích: 2000ml\r\n\r\nKích thước: 222x185x270mm\r\n\r\nCông suất: 300W\r\n\r\n\r\n- Máy xay thịt(2L) - Lưỡi dao kép thép không gỉ - Cối Thủy Tinh - Công suất 300W - 2 cấp độ xay - Bảo hành 12 tháng - Hàng chính hãng - EJM171\r\n\r\n- Sở hữu thiết kế tối giản, từng đường nét được gia công tỉ mỉ, bề mặt nhẵn bóng, mang đến vẻ đẹp sang trọng và tinh tế cho căn bếp của gia đình bạn. Đây là sản phẩm gia dụng nổi tiếng đến từ Hàn Quốc, được phát triển dựa trên phương châm hoạt động \"Vì người tiêu dùng và môi trường\", tập trung vào chất lượng và xem trọng sức khỏe người dùng, được nhiều người yêu thích và tin dùng.\r\n\r\n- Cối xay thủy tinh cao cấp\r\n\r\n- Sản phẩm có cối xay được gia công từ thủy tinh cao cấp, độ bền cao, chịu lực tốt, không chứa chất độc hại, an toàn khi sử dụng, bảo vệ sức khỏe người dùng. Với dung tích 2L, sức chứa tốt, lòng cối sâu, dùng để xay thịt và rau củ, đáp ứng tốt yêu cầu của người dùng. Đặc biệt, phần thân trên của máy xay thịt được gia công từ thép không gỉ, hạn chế bám bẩn, dễ dàng lau chùi sau khi sử dụng.\r\n\r\n- Lưỡi xay sắc bén\r\n\r\n- Bên cạnh đó, sản phẩm còn được trang bị lưỡi xay bằng chất liệu thép không gỉ, sắc bén với thiết kế chuẩn xác, khả năng nhồi và đảo thịt tốt nên có thể xay được khối lượng thịt lớn hơn so với chiều cao của lưỡi, giảm nhiệt độ sinh ra do ma sát trong quá trình xay nên hạn chế làm hỏng thực phẩm.\r\n\r\n- Hoạt động linh hoạt, dễ thao tác\r\n\r\n- Máy xay thịt hoạt động bằng điện, phần dây gắn liền với thân máy, năng suất cao, rất tiện dụng, giúp người dùng tiết kiệm điện năng và chi phí mua sắm. Dễ dàng sử dụng, bạn chỉ cần cắm điện, nhấn nút được tích hợp ở phần thân máy là có thể khởi động máy, vô cùng linh hoạt, thuận tiện điều chỉnh.\r\n\r\n'),
(14, 'kitchen', '../images/noichienkhongdau.png', 'Nồi chiên không dầu', 2200000, 0, '', 'Dung tích lớn 4L: Bữa ngon cả gia đình cùng thưởng thức\r\n\r\nKhí nóng tuần hoàn 360°C: Chiên bằng khí nóng thay thế chiên bằng dầu mỡ, nhanh hơn và tốt hơn cho sức khỏe\r\n\r\nLớp chống dính silicone: Không chứa PFOA, dễ dàng rửa sạch không phiền muộn\r\n\r\nVỉ nướng lọc dầu riêng biệt: Lọc nhanh dầu mỡ thừa trên thức ăn\r\n\r\nBộ nhớ ngắt điện: Tự động ngắt điện khi kéo giỏ chiên ra, kiểm tra thực phẩm bất cứ lúc nào và đặt vào mà không cần khởi động lại, an toàn, tiện lợi hơn\r\n\r\nThiết kế hai núm xoay ở trên cùng, kiểm soát thời gian và nhiệt độ: Thao tác dễ dàng, tiết kiệm thời gian hơn\r\n\r\nTrên mặt máy thiết kế menu 6 món chính, không mất công tìm công thức khác: Khoai tây chiên + Cake + Beef steak + Rau củ + Tôm + Đùi gà\r\n\r\nKhe tản nhiệt ở phía sau: Diện tích tản nhiệt lớn, an toàn hơn\r\n\r\n\r\n\r\n🍕Có thể làm nhiều món ăn khác nhau🍕\r\n\r\n- Khoai tây chiên (200 ° C / 23-27 phút) \r\n\r\n- Sườn nướng  (180 ° C / 23-27 phút)  \r\n\r\n- Cá nướng (180 ° C / 15-18 phút)\r\n\r\n- Tôm (180 ° C / 18-22 phút)  \r\n\r\n- Chân gà (180 ° C / 18-22 phút) \r\n\r\n- Các loạib ánh (180 ° C / 14-16 phút)\r\n\r\n- Beef steak (180 ° C / 18-22 phút) \r\n\r\n- Pizza (200 ° C / 23-27 phút)\r\n\r\n\r\n\r\n👉CHỨC NĂNG VÀ CÁCH SỬ DỤNG👈\r\n\r\n- Đặt khay nướng vào rổ chiên, cho các nguyên liệu đã chuẩn bị vào rổ chiên.\r\n\r\n- Đẩy giỏ chiên vào, nhấp vào công tắc và nhấp vào M để chọn menu mong muốn để nướng thực phẩm.\r\n\r\n- Trong quá trình chế biến thực phẩm, cần kéo rổ chiên ra và lật thực phẩm để tránh thực phẩm bị cháy khét.\r\n\r\n- Sau khi sử dụng thiết bị chờ để nguội, hãy làm sạch rổ / vỉ nướng. Không sử dụng các loại bóng cứng, sắc nhọn để làm sạch.\r\n\r\n\r\n\r\n✨THÔNG SỐ✨\r\n\r\nThương hiệu: Simplus\r\n\r\nKích thước sản phẩm: 315*256*286mm\r\n\r\nDung tích sản phẩm: 4L\r\n\r\nĐiện áp: 220V\r\n\r\nCông suất định mức: 1300W\r\n\r\nPhạm vi nhiệt độ có thể điều chỉnh: 80-200℃\r\n\r\nKhoảng thời gian điều chỉnh: 0-30 phút\r\n\r\nĐường kính bên trong nồi: 21*22cm\r\n\r\nChất liệu lớp phủ: Lớp phủ chống dính silicone\r\n\r\nKích thước dây: 1,05 mét\r\n\r\nQuy cách đầu cắm: VDE\r\n\r\nPhụ kiện đi kèm: Vỉ nướng lọc dầu\r\n\r\n'),
(15, 'kitchen', '../images/tulanh.png', 'Tủ lạnh', 7000000, 7500000, 'hangmoi', '\r\nDung tích thực 648 lít lưu trữ được nhiều thực phẩm\r\nCông nghệ Inverter vận hành êm ái, tiết kiệm điện năng\r\nNgăn đông mềm UltraChill bảo quản thịt cá được lâu hơn\r\nHệ thống làm lạnh 360 độ làm lạnh nhanh chóng, đồng đều\r\nKhoang tủ đa năng FlexFresh để bạn tự do lưu trữ mọi thứ\r\nNgăn đông size XL lưu trữ được nhiều thực phẩm đông lạnh\r\nBa dàn lạnh độc lập Triple Cooling giữ thực phẩm trọn vị \r\n\r\nBảo quản thịt và hải sản được lâu hơn\r\nVới ngăn đông mềm UltraChill ở -2 ° C, thịt và hải sản tươi sống được giữ lạnh nhưng không bị đông đá, vì thế thực phẩm tươi sống của bạn sẽ được giữ ở điều kiện tốt nhất và tươi ngon lâu hơn. Điều này giúp bạn luôn có thể thưởng thức các loại thực phẩm bảo đảm tươi ngon, trọn vẹn cả về màu sắc lẫn hương vị.\r\n\r\nMôi trường lý tưởng cho tất cả thực phẩm và thức uống của bạn\r\nKhoang tủ đa năng FlexFresh® dễ dàng đáp ứng cho nhu cầu của bạn, cho phép bạn tự do lưu trữ mọi thứ từ rau quả, thịt và cá tươi sống, đồ uống lạnh hoặc kem. Khoang tủ này có năm cài đặt nhiệt độ từ -18 ° C đến + 7 ° C để bạn luôn có thể tìm không gian thích hợp giữ cho thực phẩm của bạn tươi ngon và trọn vẹn hương vị.\r\n\r\nThêm không gian lưu trữ cho mọi nhu cầu\r\nKhoang tủ đa năng FlexFresh® 92L có thể dễ dàng chuyển đổi chức năng từ ngăn lạnh sang ngăn đông, cho phép bạn sử dụng dung lượng bổ sung tùy theo nhu cầu. Bạn sẽ có thêm nhiều lựa chọn ngăn lưu trữ cho những hoạt động giải trí cùng bạn bè và gia đình khi cần thêm không gian chứa cho thức ăn, đồ uống hoặc món tráng miệng.\r\n\r\nĐộ ẩm tối ưu cho hương vị tối ưu\r\nHệ thống ba dàn lạnh độc lập Triple Cooling sẽ giữ cho thực phẩm của bạn luôn tươi ngon và trọn vẹn hương vị. Hệ thống này tối ưu hoá độ ẩm cho tủ lạnh của bạn, bảo đảm khí lạnh từ ngăn đông sẽ không làm thực phẩm bị khô mà luôn duy trì sự tươi ngon lâu hơn.\r\n\r\nTính năng\r\nCông nghệ NutriFresh Inverter\r\nHệ thống làm lạnh 360 độ\r\nNgăn đa năng FlexFresh\r\nTủ đông cỡ XL\r\n'),
(16, 'kitchen', '../images/maylamsuachua.png', 'Máy làm sữa chua', 400000, 0, '', 'TÍNH NĂNG NỔI BẬT : \r\n\r\n- Cốc máy làm sữa chua Chefman được làm bằng thủy tinh cao cấp\r\n\r\n- Cơ chế hoạt động ổn định, giữ nhiệt độ ổn định, đảm bảo chất lượng sữa\r\n\r\n- Thiết kế thân máy làm sữa chua bằng nhựa PP cứng, bền đẹp.\r\n\r\n- Tạo ra thành phẩm sữa chua sánh mịn và thơm ngon\r\n\r\n- Tiết kiệm điện hơn và đảm bảo an toàn vệ sinh\r\n\r\n- Thiết kế chính xác, tinh xảo\r\n\r\n- Quá trình làm sữa chua hoàn toàn tự động, khép kín\r\n\r\n\r\nTRONG SẢN PHẨM ĐÃ CÓ SẴN GIẤY HƯỚNG DẪN LÀM SỮA CHUA ĐÚNG CHUẨN MỌI NGƯỜI CHỈ CẦN LÀM THEO TỜ HƯỚNG DẪN LÀ SẼ RA 1 MẺ SỮA CHUA NGON NHÉ\r\n\r\n- Khuyến cáo là nên ủ sữa chua trong vòng 8 - 12 tiếng là sữa chua sẽ đạt độ sánh mịn tốt nhất. Ủ qua đêm cũng không sao máy sau khi làm sữa xong nếu không tắt thì sẽ tự động vào chế độ chờ nên là sẽ không có vấn đề gì khi bạn ủ sữa chua qua đêm đâu nha.\r\n\r\n- Ngoài ra nếu bạn có công thức nào tốt hơn của shop cũng có thể áp dụng và nếu thành công có thể nhắn tin chia sẻ với shop đến shop truyền bá đến những khách hàng khác nha. \r\n\r\n- Máy có 2 Phân Loại:\r\n\r\n +  1 là loại bao gồm máy + 8 cốc nhựa + giấy hướng dẫn sử dụng cùng công thức làm sữa chua, mọi người có thể làm tối đa 8 cốc sữa chua 1 mẻ nha\r\n\r\n +  2 là loại  sẽ có máy + 12 cốc thủy tinh + giấy hướng dẫn sử dụng và công thức làm sữa chua nhé. Loại này là xịn nhất làm nhiều 1 lúc luôn.\r\n\r\nMọi người có thể tùy theo như cầu sử dụng và làm cho bao nhiêu người để chọn loại phù hợp nha\r\n\r\nChúc mọi người làm được những mẻ sữa chua siêu ngon mềm mịn với chiếc máy này nhaaaaaa<3'),
(17, 'cleaning', '../images/caylau-nha.jpg', 'Cây lau nhà', 150000, 0, '', 'Cây lau nhà thông minh tự vắt với khả năng lau sạch sàn nhà một cách nhanh chóng và nhẹ nhàng, phù hợp với mọi loại bề mặt sàn như sàn gạch, sàn gỗ, sàn đá, tường ốp gạch và mặt kính, phù hợp với mọi không gian và diện tích nhà ở, bộ cây lau nhà được thiết kế thông minh và cực kỳ gọn gàng giúp tiết kiệm không gian bảo quản đặc biệt là nhà chung cư hoặc các căn nhà có không gian hạn chế.\r\n\r\n\r\n1. ƯU ĐIỂM SẢN PHẨM:\r\n\r\n- Có ngăn giặt sạch nhanh và vắt cực khô\r\n\r\n- Cây lau dài bằng inox không rỉ\r\n\r\n- Tay cầm bằng mút mềm không đau tay\r\n\r\n- Vải lau thấm hút tốt và giữ được tóc, lông động vật\r\n\r\n- Bàn lau lớn có thể xoay 360 độ\r\n\r\n- Thiết kế thông minh, gọn gàng và tinh tế, tiết kiệm không gian\r\n\r\n- Lau nhà mà tay không phải chạm nước và hóa chất tẩy rửa, bảo vệ da tay\r\n\r\n\r\n\r\n2. THÔNG TIN SẢN PHẨM\r\n\r\n- Tên sản phẩm: Bộ cây lau nhà tự vắt thông minh Pro PR01\r\n\r\n- Chất liệu: Nhựa dẻo và thép chống rỉ\r\n\r\n- Miếng lau Microfiber thấm hút cực tốt: 2 miếng lau (33cm X 12cm)\r\n\r\n- Kích thước thùng nước:  6L (38.5cm X 18cm X 23cm)\r\n\r\n- Kích thước cây lau dài: 161 cm\r\n\r\n- Kích thước bàn lau: 33cm X 12cm\r\n\r\n- Không có van xả nước\r\n\r\n- Sản phẩm bao gồm: 1 thùng nước, 1 cây lau, 2 miếng lau\r\n\r\n\r\n\r\n3. HƯỚNG DẪN SỬ DỤNG\r\n\r\n- Bước 1: Sau khi mở hộp sản phẩm cây lau nhà, bạn lắp ráp các thanh cây lau lại với nhau (theo hướng dẫn cuối video sản phẩm)\r\n\r\n- Bước 2: Cho lượng nước sạch vừa đủ vào ngăn giặt (ngăn WASH) có thể pha một chút nước lau sàn để tạo mùi hương, lưu ý không đổ nước vào ngăn vắt khô (ngăn DRY)\r\n\r\n- Bước 3: Gấp dọc bàn lau và cho bàn lau vào ngăn giặt để làm ướt miếng lau, tiếp theo cho bàn lau vào ngăn khô để làm khô miếng vải lau trước khi lau nhà (tham khảo trên video sản phẩm)\r\n\r\n- Bước 4: Sau khi lau nhà xong làm sạch miếng vải lau bằng ngăn giặt (ngăn WASH) và vắt khô miếng vải lau bằng ngăn vắt\r\n\r\n- Bước 5: Đổ nước bẩn ra khỏi thùng nước và để miếng lau nhà khô thoáng để đảm bảo vệ sinh và giữ được độ bền cho sản phẩm.'),
(18, 'cleaning', '../images/maygiat.jfif', 'Máy giặt', 8000000, 8500000, 'hangmoi', 'Máy giặt có khối lượng giặt 9 kg thích hợp cho gia đình 3 - 5 thành viên\r\n\r\nLồng giặt chất liệu thép không gỉ bảo vệ quần áo, hạn chế sờn rách vải\r\n\r\nHệ thống thác nước thông minh 3 chiều giúp đánh bay vết bẩn hiệu quả\r\n\r\nNắp kính cường lực đóng mở nhẹ nhàng, hạn chế kẹt tay trong khi sử dụng\r\n\r\n\r\n\r\nĐặc điểm sản phẩm\r\n\r\nModel:	ES-Y90HV-S\r\n\r\nMàu sắc:	Đen Bạc\r\n\r\nNhà sản xuất:	Sharp\r\n\r\nXuất xứ:	Trung Quốc\r\n\r\nNăm ra mắt :	2024\r\n\r\nLoại máy giặt:	Cửa trên\r\n\r\nKhối lượng giặt:	9 Kg\r\n\r\nChế độ giặt:	Nhiều chế độ\r\n\r\nĐộng cơ dẫn động:	Đang cập nhật\r\n\r\nCông nghệ Inverter:	Không\r\n\r\nTốc độ quay vắt:	700 vòng/phút\r\n\r\nChế độ giặt nhanh:	Có\r\n\r\nVệ sinh lồng giặt:	Có\r\n\r\nChất liệu lồng giặt:	Thép không gỉ\r\n\r\nKhóa an toàn:	Khóa trẻ em\r\n\r\nKhối lượng sản phẩm (kg):	38 kg\r\n\r\nKích thước sản phẩm:	570 x 590 x 924 mm'),
(19, 'cleaning', '../images/robotlaunha.png', 'Robot lau nhà', 6500000, 0, '', '📌Đặc điểm nổi bật của Robot lau nhà - máy lau nhà thông minh A8\r\n\r\n- Hoạt động xoay tròn mô phỏng chế độ lau tay, tao ra lực mạnh hơn, làm sạch hơn với diện tích lớn hơn\r\n\r\n- Tiếng ồn thấp\r\n\r\n- Không cần điều chỉnh cài đặt kiểm soát, máy tự hoạt động\r\n\r\n- Tiêu thụ điện năng thấp, tiết kiệm điện\r\n\r\n- Giẻ lau khô và giẻ lau ướt dễ dàng thay đổi phù hợp với trạng thái sàn nhà\r\n\r\n- Robot xoay mô phỏng bàn tay người, làm sạch hiệu quả\r\n\r\n- Làm sạch được nhiều loại sàn nhà với các loại bụi, lông tóc, vết bẩn...\r\n\r\n- Chế độ phun sương tinh dầu thanh lọc không khí mọi nơi robot đi qua\r\n\r\n- Robot gặp vật cản tự động chuyển hướng, làm việc chăm chỉ và hiệu quả hơn\r\n\r\n- Mỗi lần sạc đầy pin sử dụng được 2 giờ với dung lượng pin lớn\r\n\r\n📌Thông Tin Sản Phẩm:\r\n\r\n- Chất liệu: ABS\r\n\r\n- Pin: Pin lithium 2400 mAh\r\n\r\n- Thời gian làm việc: 180 phút\r\n\r\n- Điện áp định mức: 3.7V\r\n\r\n- Công suất định mức: 52HZ\r\n\r\n- Tần số định mức: 5W\r\n\r\n- Diện tích áp dụng: 90-120 mét vuông\r\n\r\n- Âm thanh làm việc 50 decibel, tắt tiếng không làm phiền cuộc sống\r\n\r\n- Thân máy siêu mỏng dễ dàng đi vào điểm mù\r\n\r\n- Có chức năng phun sương và tạo ẩm\r\n\r\n- Trọng lượng: 1180 gram\r\n\r\n-  Kích thước: 25x25x6 cm\r\n\r\n📌Bộ sản phẩm gồm: 1 Máy, 1 giẻ lau khô, 1 giẻ lau ướt, 1 dây sạc, 1 hướng dẫn sử dụng'),
(20, 'cleaning', '../images/mayruaxe.jpg', 'Máy rửa xe', 3000000, 3500000, 'hangmoi', '🏅🏅ƯU ĐIỂM NỔI BẬT:\r\n\r\n+ Tiết kiệm thời gian và lượng điện tiêu thụ: áp lực cao giúp làm sạch vết bẩn nhanh chóng, cho phép tẩy rửa cả các vết bẩn ở những vị trí khó thao tác như gầm xe, các góc cạnh một cách dễ dàng, do đó thời gian rửa xe sẽ nhanh hơn rất nhiều so với máy rửa thông thường. Do vậy nó sẽ giúp cho khách hàng vừa tiết kiệm thời gian vừa tiết kiệm điện và mang lại hiệu quả kinh tế cao nhất.\r\n\r\n+ Thiết kế kiểu dáng nhỏ gọn, đẹp mắt sang trọng hiện đại. Phụ kiện treo lên thân máy gọn gàng tiện lợi khi cất giữ hoặc di chuyển. Máy có tính thẩm mỹ cao.\r\n\r\n+ Máy được thiết kế đồng hồ đo áp lực nước giúp người dùng nắm bắt được áp lực thực tế của dòng nước.\r\n\r\nCùng với thông số kỹ thuật cao cấp mang đến cho người dùng những ưu điểm vượt trội \r\n\r\n\r\n🏅🏅THÔNG SỐ KỸ THUẬT:\r\n\r\n1.Mô tơ từ bền bỉ, êm ái\r\n\r\n2.Áp lực nước: 90-130bar\r\n\r\n3.Công suất định danh/tối đa: 1000-2500w\r\n\r\n4.Lưu lượng nước: 500L/H\r\n\r\n5.Điện áp: 220V-240V\r\n\r\n6.Đường kính motor :96mm\r\n\r\n7.Chiều dài motor: 80mm\r\n\r\n8. Kích thước SP/bao bì :33×12×22cm/37x18x38cm\r\n\r\n9. Trọng lượng sản phẩm/bao bì: 5.5kg/7.5kg\r\n\r\n\r\n🏅🏅PHỤ KIỆN:\r\n\r\n- Ống hút nước vào 2m kèm bộ kit kết nối vòi nước và bộ lọc nước đầu vào tiện lợi, vòi nước áp lực đầu ra 15m kèm súng phun đa năng (tia + toả), giúp điều chỉnh tia nước từ tia sang toả, tự động từ nhẹ tới mạnh đánh tan mọi vết bẩn.\r\n\r\n\r\n\r\n- Ngoài ra khi mua hàng, khách hàng còn được tặng kèm quà khuyến mại bình Xà Phòng, Tặng gói bảo hiểm về tài sản và người trị giá 2 tỷ của Tổng Cty CP Bảo hiểm Quân Đội'),
(21, 'cleaning', '../images/cayvetrachbe.png', 'Cây vệ sinh bể', 400000, 0, '', 'Chất liệu: NHỰA + Bọt biển\r\nSố lượng: 1 cái\r\nMàu sắc: Xanh lá cây, xanh lam\r\nTổng chiều dài: 40cm\r\nKích thước đầu: 7,5cmx6,5cmx3,5cm\r\nCác tính năng: Thuận tiện và dễ dàng để xử lý. Tốt để làm sạch cho Hồ cá, kính câu cá và bể.\r\nGói bao gồm: 1 cái bàn chải làm sạch bể cá cảnh\r\nCác tính năng:\r\nThuận tiện và dễ dàng để xử lý.\r\nTốt để làm sạch cho Hồ cá, kính câu cá và bể.\r\nVui lòng rửa bàn chải sau khi sử dụng nó mỗi lần.\r\nBộ đồ cho phòng khách, khách sạn và phòng tắm nơi có đồ thủy tinh.\r\nPhân của cá khó làm sạch, bạn cần bàn chải này rất dễ làm sạch.\r\n \r\nLưu ý: \r\nVui lòng cho phép chênh lệch 1 ~ 2 cm do đo lường thủ công.\r\nDo màn hình hiển thị khác nhau và ánh sáng khác nhau, hình ảnh có thể khác một chút với màu sắc thực tế của mặt hàng. Cảm ơn cho sự hiểu biết của bạn.\r\nBàn chải bể cá Bàn chải làm sạch bể cá Bàn chải tảo Dụng cụ làm sạch bể cá Dụng cụ làm sạch kính Bàn chải chà rửa nổi Làm sạch cho bể cá Phụ kiện bể cá Tay cầm dài Không trượt'),
(22, 'cleaning', '../images/binhxitsatchat.jfif', 'Bình xịt sát khuẩn', 300000, 0, '', 'Combo gồm 2 sản phẩm: Xịt kháng khuẩn Dizigone Baby 300ml (vòi xịt cải tiến) và xit kháng khuẩn Dizigone 100ml. Hai chai có thành phần giống nhau, chỉ khác dung tích và thiết kế chai xịt. \r\n\r\n\r\n\r\n1. Thành phần \r\n\r\nXịt kháng khuẩn Dizigone có thành phần chính là dung dịch điện hóa chứa HOCl, được tạo nên từ công nghệ điện hóa EMWE Châu Âu. HOCl là hoạt chất kháng khuẩn tự nhiên do chính cơ thể sinh ra trong đáp ứng miễn dịch, nên thân thuộc, lành tính và an toàn. \r\n\r\nXịt kháng khuẩn Dizigone Baby đã được kiểm chứng chất lượng tại các cơ quan kiểm định uy tín: \r\n\r\n- Về hiệu quả kháng khuẩn: Tiêu diệt 99.9% mầm bệnh chỉ trong vòng 30 giây (kiểm chứng tại Trung tâm Quatest 1 - Bộ Khoa Học Công Nghệ) \r\n\r\n- Về tính an toàn: Dịu nhẹ và an toàn cho bé từ sơ sinh, không gây kích ứng, không gây tác dụng phụ (kiểm chứng tại Trung tâm Dược lý - ĐH Y Hà Nội). \r\n\r\n\r\n\r\n2. Công dụng \r\n\r\nXịt kháng khuẩn Dizigone Baby ứng dụng đa dạng trong phòng bệnh, bảo vệ bé và đẩy lùi các tổn thương da thường gặp cho cả gia đình: \r\n\r\n- Xịt tay, chân và cơ thể bé\r\n\r\n- Xịt đồ chơi, đồ dùng hàng ngày của bé: núm ti giả, đồ gặm nướu, bình sữa,...\r\n\r\n- Xịt khử khuẩn môi trường sống và các bề mặt tiếp xúc: xịt bàn ghế, tay nắm cửa, tay vịn cầu thang,...\r\n\r\n- Xịt phòng ngừa và đẩy lùi các tổn thương da: hăm tã, hăm da, chàm sữa, mẩn ngứa, rôm sảy, mẩn ngứa, muỗi đốt; thủy đậu, tay chân miệng; chốc lở, mụn nhọt,...\r\n\r\n- Vệ sinh khoang miệng và chăm sóc tổn thương khoang miệng: nấm miệng, loét miệng, nhiệt miệng, hôi miệng,...\r\n\r\n\r\n\r\n3. Cách dùng \r\n\r\n- Để kháng khuẩn phòng bệnh: Xịt trực tiếp Dizigone baby lên tay chân, cơ thể, đồ chơi, đồ dùng hay các bề mặt cần vệ sinh, làm sạch.\r\n\r\n- Để làm dịu da & đẩy lùi tổn thương da: Xịt trực tiếp Dizigone Baby lên vùng da cần chăm sóc, hoặc thấm dung dịch Dizigone ra bông y tế để lau rửa sát khuẩn da 3-5 lần/ngày.\r\n\r\n- Để vệ sinh khoang miệng: Xịt dung dịch Dizigone Baby ra gạc để rơ miệng cho bé; hoặc súc miệng trực tiếp với dung dịch Dizigone Baby 3-5 lần/ngày.\r\n\r\nSau khi xịt rửa Dizigone Baby, chỉ cần đợi dung dịch khô lại tự nhiên, không cần rửa lại bằng nước.\r\n\r\n\r\n\r\n4. Lưu ý khi dùng \r\n\r\n- Xịt kháng khuẩn Dizigone Baby có mùi chloride hoạt tính đặc trưng của thành phần chính HOCl. Đây là hoạt chất kháng khuẩn tự nhiên của hệ miễn dịch nên an toàn tuyệt đối và thân thuộc với cơ thể. \r\n\r\n- Sản phẩm bảo quản nơi khô ráo, thoáng mát, tránh ánh nắng trực tiếp và nên sử dụng trong vòng 3 tháng từ sau khi mở nắp. \r\n\r\n'),
(23, 'climate', '../images/may-hut-mui-co-dien-sunhouse-shb6118i.jpg', 'Máy hút mùi Sunhouse SHB6118', 3590000, 0, '', 'Máy hút mùi Sunhouse SHB6118, thiết kế hiện đại, công suất 1100m3/h, giúp loại bỏ mùi hiệu quả, độ ồn thấp, dễ dàng vệ sinh.'),
(24, 'climate', '../images/densuoinhatam.webp', 'Đèn sưởi nhà tắm', 2500000, 0, '', 'THÔNG TIN SẢN PHẨM\r\n\r\n+ Đèn sưởi có 2 mẫu 2 bóng và 3 bóng\r\n\r\n+ Kích cỡ: 30 x 60 x 30 cm\r\n\r\n+ Công suất: 275 W/ 1 bóng đèn\r\n\r\n\r\n\r\nĐẶC ĐIỂM SẢN PHẨM\r\n\r\nĐèn sưởi nhà tắm 2 bóng ,3 bóng cao cấp shop cam kết hàng đúng như mô tả 100%\r\n\r\nĐèn sưởi cao cấp đui sứ,dây cáp điện bọc sợi thuỷ tinh chống cháy nổ an toàn tuyệt đối.\r\n\r\n+ Bảng điều khiển: 2 công tắc điều khiển 3 bóng 3 chế độ khác nhau.\r\n\r\n+ Bóng đèn:  bóng tráng kim cương nhân tạo có tác dụng tăng độ ấm và giảm chói mắt khi sử dụng\r\n\r\n+ Tuổi thọ Bóng đèn: > 10.000 h (tương đương 6 năm)\r\n\r\n+ Dây điện: dài 2m, đầu cắm tròn, 2 chân theo tiêu chuẩn châu Âu\r\n\r\n+ Phụ kiện đi kèm: móc treo 2 vít nở. Diện tích phòng: từ 2 - 4 m2 / 4-6 m2 tùy loại 2 bóng hay 3 bóng\r\n\r\n\r\n\r\nTÍNH NĂNG NỔI BẬT\r\n\r\n- Thiết kế thời trang, bề mặt bóng tráng kim cương nhân tạo, logo nổi và sắc nét, bóng sưởi bớt chói mắt và có độ ấm cao hơn những bóng thông thường.\r\n\r\n- Kích thước nhỏ gọn tiết kiệm diện tích cho phòng tắm nhà bạn\r\n\r\n- Sử dụng bóng hồng ngoại, sưởi ấm nhanh , không đốt cháy oxy\r\n\r\n- Thiết kế sang trọng phù hợp với mọi phòng tắm\r\n\r\nVới việc sử dụng công nghệ mới, bóng đèn được tráng một lớp hợp kim màu vàng sẽ làm giảm độ chói của ánh sáng mà vẫn không làm giảm đi nhiệt độ do bóng tỏa ra. Đèn sưởi nhà tắm có thể sử dụng ở phòng tắm, phòng ngủ, phòng khách.\r\n\r\n\r\n\r\nLƯU Ý KHI SỬ DỤNG\r\n\r\n– Không phun nước trực tiếp vào bóng đèn sưởi nhà tắm bởi vì có thể trong trường hợp dây điện rò rỉ dẫn đến nguy hiểm tính mạng.\r\n\r\n– Vệ sinh đèn sưởi nhà tắm một năm 2 lần vào thời điểm trước khi bạn chuẩn bị sử dụng và thời điểm sau khi kết thúc sử dụng (cuối mùa đông)\r\n\r\n– Không sử dụng đèn sưởi nhà tắm quá lâu trong một lần tắm. Khoảng thời gian hợp lý cho một lần bật đèn sưởi để tắm là 10 – 15 phút.'),
(25, 'climate', '../images/quat-dung.webp', 'Quạt đứng', 800000, 900000, 'hangmoi', '***Mô Tả Sản Phẩm:\r\n\r\n\r\n\r\n1. Ngành sản phẩm thiết bị điện gia dụng là một trong những ngành mũi nhọn của Panasonic tại thị trường Việt Nam, trong số đó có các sản phẩm quạt điện Panasonic luôn nhận được sự tin tưởng, lựa chọn của người tiêu dùng bởi chất lượng Nhật Bản, độ bền, độ thực dụng, độ an toàn cao, nhiều tiện ích và công nghệ hiện đại. Panasonic cung cấp đa dạng các loại sản phẩm quạt điện cũng như mẫu mã kiểu dáng khác nhau từ quạt trần, quạt cây, quạt treo tường, quạt trần đảo,... đáp ứng đa dạng nhu cầu của người sử dụng.\r\n\r\n2. Quạt đứng Panasonic màu xanh và màu hồng trang nhã, kiểu dáng mềm mại sang trọng, sử dụng thích hợp cho nhiều không gian kiến trúc. Với công suất lớn và 5 cánh quạt đường kính 30cm tạo lưu lượng gió 40 cm3/phút nhanh chóng làm mát khắp không gian phòng sử dụng. Quạt còn được trang bị thêm điều khiển từ xa tiện lợi, có thể tùy chỉnh chế độ mà không cần tới gần quạt \r\n\r\n------------------------------------------------\r\n\r\n*** Thông số kỹ thuật:\r\n\r\n- Công suất (W): 37W\r\n\r\n- Màu sắc: Hồng, Xanh, Đỏ\r\n\r\n- Điều khiển Remote: Có\r\n\r\n- Chức năng tạo gió tự nhiên: 1/f Yuragi\r\n\r\n- Cấp độ gió: 3\r\n\r\n- Chế độ hẹn giờ (h): 1h-2h-4h\r\n\r\n- Lưu lượng gió: 40m3/min\r\n\r\n- Đường kính cánh quạt (cm): 30cm\r\n\r\n- Chiều cao quạt: 91cm - 105cm\r\n\r\n- Động cơ: Bạc đạn\r\n\r\n------------------------------------------------\r\n\r\n***Công Ty cam kết bán hàng chính hãng 100%, sản phẩm bảo hành 1 năm khách hàng có thể liên hệ qua mục chat lấy số điện thoại liên hệ tới tận cửa hàng để bảo hành, hoặc gửi bảo hành tại trung tâm hãng toàn quốc.\r\n\r\n***Quý khách có nhu cầu lấy hóa đơn vui lòng để lại thông tin Công Ty và mail bên mình sẽ gửi hóa đơn điện tử qua mail.'),
(26, 'climate', '../images/maylanhtreem.png', 'Máy lạnh cho trẻ em', 4500000, 0, '', '✅ƯU ĐIỂM VƯỢT TRỘI:\r\n\r\n- Có thể lắp đặt hoặc không cần lắp đặt (lắp đặt dễ dàng)\r\n\r\n- Độ ồn thấp ≤55dB\r\n\r\n- Máy nén GMCC - liên doanh TOSHIBA\r\n\r\n- Có chế độ ngủ êm ái\r\n\r\n- Hẹn giờ bật tắt 24 tiếng\r\n\r\n\r\n\r\n✅ ĐIỀU KHIỂN LINH HOẠT:\r\n\r\n+ Màn hình LED cảm ứng trực quan\r\n\r\n+ Kết nối Wifi - điều khiển qua App (riêng 7.000BTU không có)\r\n\r\n+ Remote điều khiển từ xa\r\n\r\n\r\n\r\n✅ TÍCH HỢP 4 CHỨC NĂNG:\r\n\r\n+ Khả năng làm lạnh: công suất 14.000BTU, sử dụng trong các không gian kín 30m2\r\n\r\n+ Quạt làm mát: lưu lượng gió 390m³/h\r\n\r\n+ Lọc sạch không khí: màng lọc thô lọc sạch không khí trước khi đi vào hệ thống làm mát, tăng hiệu suất làm lạnh\r\n\r\n+ Hút ẩm: công suất hút ẩm lên đến 32.5 lít/ngày\r\n✅ KHẢ NĂNG LÀM LẠNH\r\n\r\nMáy sử dụng môi chất R290 hoặc R410a có hiệu suất làm lạnh tốt và thân thiện với môi trường. Khả năng làm lạnh công suất 14.000BTU giúp làm mát nhanh chóng \r\n\r\n\r\n\r\n✅ QUẠT LÀM MÁT\r\n\r\nTrong các không gian không kín hoàn toàn, máy vẫn có thể sử dụng để làm mát cục bộ cho người ngồi phía trước máy cách cửa gió từ 1-3m.\r\n\r\n\r\n\r\n✅ LỌC SẠCH KHÔNG KHÍ\r\n\r\nMáy sử dụng mạng lọc thô tiêu chuẩn giúp lọc sạch không khí trước khi đi vào hệ thống làm mát, vừa giúp làm sạch không khí, hạ nhiệt nhanh, bảo vệ và kéo dài tuổi thọ cho thiết bị\r\n\r\n\r\n\r\n ✅ HÚT ẨM TRONG ĐIỀU KIỆN TRỜI NỒM\r\n\r\nMáy điều hòa di động Fujihome PAC12 có thể được sử dụng như một máy hút ẩm thực thụ với công suất hút ẩm lên đến 32.5 lít/ngày, có thể thoát nước thải liên tục qua ống hoặc xuống bình chứa bên dưới máy (dung tích 0.6 lít).\r\n\r\n\r\n\r\n✅ THÔNG SỐ KỸ THUẬT\r\n\r\n· Máy lạnh di động 4 trong 1 (làm lạnh, hút ẩm, quạt, lọc không khí )\r\n\r\n· Máy nén GMCC - liên doanh TOSHIBA nổi tiếng, tiếng ồn rất thấp ≤55dB\r\n\r\n· Điều khiển từ xa, đầy đủ chức năng, nút đơn giản khi sử dụng\r\n\r\n· 2 tốc độ quạt mạnh mẽ, Hẹn giờ 24 giờ; Chế độ ngủ\r\n\r\n Tự động dừng và cảnh báo bằng mã chỉ báo trên bảng hiển thị khi nước đầy\r\n\r\n· Môi chất lạnh R290/R410a thân thiện với môi trường. TC EER (2,6): Loại A\r\n\r\n· Điều khiển điện tử với màn hình LED\r\n\r\n· Tự động khởi động lại: Thiết bị tự động khởi động lại sau khi mất điện\r\n\r\n· Điện áp: AC220-240V / 50Hz\r\n\r\n· Mức tiêu thụ điện và dòng điện: 1560W/6.88A\r\n\r\n· Khả năng làm mát: 4100W/14000BTU\r\n\r\n· Chứng chỉ UKCA, CE, ERP, RoHS\r\n\r\n· Công suất hút ẩm: 2.5 L / ngày\r\n\r\n· Bên trong thùng sản phẩm bao gồm:Thân máy x 1; Hướng dẫn sử dụng x 1; Ống thoát nước x 1; Bộ lắp đặt cửa sổ & ống thông hơi x 1; Điều khiển từ xa\r\n\r\n- Tiếng ồn: ≤56dB, Lưu thông không khí 390m³ / h\r\n\r\n· Cửa gió thoát khí có thể điều chỉnh lên và xuống\r\n\r\n· Ống xả khí nóng: 1 PCS (kéo mở dài 1.5m, nén gọn dài 24cm)\r\n\r\n· ống xả nước có chiều dài 65cm\r\n\r\n· Bể chứa nước bên trong dung dích 0.6L\r\n\r\n· Trọng lượng sản phẩm: 29kgs\r\n\r\n· Kích thước sản phẩm: 66.5cm x 44cm x 33.5cm'),
(27, 'climate', '../images/maytaoion.png', 'Máy tạo ion', 3800000, 0, '', 'Kiểu dáng nhỏ gọn\r\nMáy Tạo Ion Và Hút Ẩm Sharp DW-D12A-W (4L) có kiểu dáng nhỏ gọn, không chiếm nhiều không gian diện tích trong nhà giúp bạn lắp đặt dễ dàng và nhanh chóng hơn.\r\n\r\nTự động đảo cửa gió\r\nMáy trang bị tính năng tự động đảo cửa gió để có thể làm khô không khí, hút ẩm trên một diện tích rộng lớn nên bạn không cần khuân chuyển sản phẩm đến nhiều vị trí khác nhau trong phòng.\r\n\r\nHệ thống hút ẩm tự động\r\nThiết bị có khả năng tự động điều chỉnh hoạt động của máy nén và tốc độ quạt nhằm duy trì độ ẩm ổn định cho bầu không khí được thông thoáng và dễ chịu, tốt cho sức khỏe người dùng.\r\n\r\nSấy khô quần áo nhanh chóng\r\nMáy tạo ion và hút ẩm Sharp DW-D12A-W sẽ giúp bạn nhanh chóng sấy khô quần áo với tốc độ quạt lớn kết hợp tính năng hút ẩm nhanh, hút sạch hơi nước trong quần áo trên từng sợi vải một cách hiệu quả nhất. Bạn sẽ không lo lắng quần áo bị ẩm ướt vào những ngày mưa bão kéo dài.'),
(28, 'other', '../images/guongdebanled.webp', 'Gương trang điểm để bàn LED', 450000, 0, '', 'Gương trang điểm để bàn có đèn LED, thiết kế hiện đại, ánh sáng tự nhiên, giúp trang điểm dễ dàng ngay cả trong điều kiện thiếu sáng.'),
(29, 'climate', '../images/may-suoi.jpg', 'Máy sưởi', 2800000, 0, '', '- Sử dụng công nghệ PTC (Ceraminc) làm nóng bằng cách nung nóng các tấm gốm bên trong máy, không gây khô da\r\n\r\n- Vỏ ABS chịu nhiệt chống cháy, an toàn, chạm vào không bỏng\r\n\r\n- Máy sưởi gốm Lebenlang tạo gió ấm chỉ trong hai giây\r\n\r\n- Chế độ TỰ ĐỘNG QUAY bán kính rộng 75 độ, phân bổ nhiệt đều ra căn phòng\r\n\r\n- Tích hợp cảm ứng và điều khiển từ xa điều chỉnh chế độ sưởi\r\n\r\n- Màn hình LCD hiển thị nhiệt độ, giúp bạn dễ dàng theo dõi và điều chỉnh nhiệt độ phù hợp\r\n\r\n- Có chức năng làm khô không khí vào mùa ẩm (mùa nồm).\r\n\r\n- Chân đế tích hợp chốt an toàn, quạt sẽ tự động tắt khi bị nghiêng hoặc đổ - An toàn tuyệt đối khi sử dụng\r\n\r\n- Tấm lưới kim cương SIÊU BỀN, không gỉ, không gây oxy hóa\r\n\r\n- TIẾT KIỆM 50% điện năng so với các dòng máy sưởi trên thị trường\r\n\r\n- Độ bền sử dụng trên 6000 giờ với nhiệt độ ổn định và tuổi thọ lâu dài\r\n🍀 Thay vì phải sử dụng một chiếc sưởi lớn chiếm nhiều diện tích, làm ấm mất nhiều thời gian để có thể cảm nhận. Thì việc sở hữu Máy sưởi mini Lebenlang LBL0279 sẽ giúp bạn giải quyết tất cả. Điều này được thực hiện nhờ vào công nghệ sưởi ấm gốm PTC, hiệu suất cao, tạo nhiệt nhanh.\r\n\r\n🍀Máy sưởi mini Lebenlang LBL0279 chỉ chiếm một diện tích rất nhỏ trên bàn làm việc, bàn học, phòng ngủ hay bàn ngủ của bạn. Đặc biệt nhà sản xuất còn cho biết thêm Máy sưởi mini Lebenlang LBL0279 có độ bền sử dụng trên 6000 giờ với nhiệt độ ổn định và tuổi thọ lâu dài, giúp bạn có thể yên tâm sử dụng.\r\n\r\n🔶🔶 MÁY SƯỞI MINI LEBENLANG LBL0279 HOẠT ĐỘNG VỚI 5 KHÔNG:\r\n\r\n- KHÔNG đốt oxy, không gây khó thở\r\n\r\n- KHÔNG gây khô da - cân bằng độ ẩm\r\n\r\n- KHÔNG gây hiện tượng nhức mỏi với người thấp khớp\r\n\r\n- KHÔNG gây bỏng\r\n\r\n- KHÔNG gây tiếng ồn');
INSERT INTO `sanpham` (`idsp`, `danhmuc`, `hinhanh`, `tensp`, `giasp`, `giacu`, `loai`, `MoTa`) VALUES
(30, 'climate', '../images/quatdien.jfif', 'Quạt điện', 500000, 600000, 'hanggiamgia', 'Quạt điện mini công suất 40W , 3 tốc độ gió siêu mát không ồn để văn phòng , giường ngủ cho bé\r\n\r\n✅ ƯU ĐIỂM NỔI BẬT Quạt điện B1216 :\r\n  AN TOÀN SỬ DỤNG SẢN PHẨM\r\n\r\n+   Lồng quạt có khe hở nang quạt nhỏ, ngón tay người không thể chạm vào cánh quạt.\r\n\r\n+   Người sử dụng không chạm được vào bộ phận mang điện bên trong quạt\r\n\r\n+   Có cầu chì bảo vệ chống cháy khi cách quạt bị hãm, trong lúc động cơ hoạt động.\r\n\r\n+   Quạt đủ độ ổn định (nghiêng 10 độ không ngã quạt)\r\n\r\n+   Công tắc, các phần bảo vệ mối nối dây làm bằng chất liệu chống cháy. Đủ khả năng chịu nhiệt, đảm bảo an toàn khi sử dụng.\r\n\r\n✅ THÔNG SỐ CHI TIẾT Quạt điện B1216 :\r\n\r\n-        Model : B1216\r\n\r\n-        Hãng sản xuất : SENKO\r\n\r\n-        Xuất xứ : Việt Nam\r\n\r\n-        Loại : Quạt bàn\r\n\r\n-        Công suất : 40W\r\n\r\n-        Điện áp : 220V/ 50Hz\r\n\r\n-        Lưu lượng gió : 37.1 m3/min\r\n\r\n-        Đường kính cánh : 29 cm\r\n\r\n-        Số lượng cánh : 3 cánh\r\n\r\n-        Màu sắc : Cốm, Môn, Xám, Xanh ngọc, Kem nâu\r\n\r\n-        Quy cách (01 cái/thùng)	302 x 302 x 445 (mm) – Cân nặng: 3,1 kg'),
(31, 'other', '../images/binh-giu-nhiet.png', 'Bình giữ nhiệt', 300000, 0, '', 'Bình giữ nhiệt 600ml inox 304 Elmich EL8357, Hàng chính hãng, Giữ lạnh đến 40h, nắp chống tràn, có quai xách - JoyMall\r\n\r\n\r\n\r\nTHÔNG TIN SẢN PHẨM\r\n\r\nMàu sắc: Xanh lá, Navy\r\n\r\nDung tích 600ml\r\n\r\nCông dụng Giữ nhiệt nóng, lạnh\r\n\r\nChất liệu Inox 304\r\n\r\nTrọng lượng tịnh sản phẩm 365(g)\r\n\r\nKích thước sản phẩm 75x70x235(mm)\r\n\r\nTính năng chính Giữ nhiệt nước trong bình\r\n\r\nCác tính năng khác\r\n\r\nGiữ nhiệt nóng: 8.5h\r\n\r\nGiữ nhiệt lạnh: 37h\r\n\r\nThời gian bảo hành 6 tháng đối với lỗi giữ nhiệt\r\n\r\nSản xuất tại Trung Quốc\r\n\r\n\r\n\r\n\r\n\r\nTÍNH NĂNG NỔI BẬT\r\n\r\n1. Thiết kế thời trang, màu sắc trẻ trung phù hợp với cả hai nam và nữ.\r\n\r\n3. Dung tích 600ml phù hợp nhu cầu sử dụng.\r\n\r\n4. Công nghệ hút chân không đỉnh cao, giữ lạnh đến 40h và giữ nóng đến 10h\r\n\r\n5. Đóng/mở dễ dàng chỉ với 1 chạm\r\n\r\n6. Chống tràn tuyệt đối, an toàn khi sử dụng kể cả khi lật ngược cốc, cốc đổ.\r\n\r\n7. Miệng rộng thêm đá dễ dàng, vệ sinh nhanh chóng.\r\n\r\n8. Quai xách tiện lợi, dễ dàng mang theo mọi nơi\r\n\r\n9. Thích hợp để đựng với đa dạng thức uống khác nhau\r\n\r\n10. Thiết kế miệng uống dạng máng rót nên uống nước hay rót nước đều rất thuận tiện, miệng rót nhỏ nên ngăn cản được đá viên, không lo nước bị đọng lại trên nắp.\r\n\r\n11. Đế silicon chống trượt và giảm tiếng ồn tối đa, tránh ảnh hưởng tới những người xung quanh.\r\n\r\n12. Nắp tích hợp lọc trà tiện lợi ngăn đá, hoặc lá trà khi uống'),
(32, 'other', '../images/binhphunxit.png', 'Bình phun xịt', 200000, 0, '', '\r\n150 / 200 ml nước dạng chai nhựa trong suốt rỗng\r\nKích thước: 5,8x17cm (150ml)\r\n5,6x18,3cm (200ml)\r\n\r\nChất liệu: Nhựa\r\n\r\nMàu sắc: Trong suốt\r\nBình xịt cầm tay có bộ truyền động vòi bơm.\r\n\r\nSố lượng: 1\r\nBao bì bao gồm:\r\n1 chai xịt\r\n\r\n★ Chú ý: Do sự khác biệt về cài đặt ánh sáng và màn hình, màu sắc của sản phẩm có thể hơi khác so với hình ảnh. Cảm ơn bạn cho sự hiểu biết của bạn. Do đo lường thủ công, xin vui lòng cho phép các lỗi số nhỏ.'),
(33, 'other', '../images/kettuidien.jfif', 'Két sắt mini', 1000000, 0, '', 'Thông tin sản phẩm: Két Sắt Mini Khóa Cơ CENTURY Việt Tiệp 2 Tầng K88BLN-KC Nặng 55kg (Miễn Phí Giao Hàng)\r\n\r\n✅ Hàng chính hãng Công Ty Két Sắt Việt Tiệp, bảo hành chính hãng tại nhà\r\n\r\n✅ Miễn Phí Giao Hàng Toàn Quốc (nhắn tin shop tư vấn cách miễn phí ship)\r\n\r\n\r\n\r\nKích thước sản phẩm: Két Sắt Mini Khóa Cơ CENTURY Việt Tiệp 2 Tầng K88BLN-KC Nặng 55kg \r\n\r\n◾️ Kích thước ngoài: cao 50 (tiến bảo) * rộng 38 (Tài đức)* sâu 36 cm (Nghinh phúc)\r\n\r\n◾️ Kích thước trong: Cao 41 x Rộng 31 x Sâu 29 cm\r\n\r\n◾️ Phía trên có 01 ngăn kéo nhỏ có khoá riêng biệt \r\n\r\n◾️ Trọng lượng: 55kg +-10%\r\nTính năng: Két Sắt Mini Khóa Cơ Việt Tiệp 2 Tầng K88BLN-KC Nặng 55kg \r\n\r\n◾️ Két sắt Việt Tiệp K88BLN-KC khóa cơ: két sắt sử dụng khóa cơ theo mã ngẫu nhiên của nhà máy kết hợp với khóa chìa 4 cạnh cao cấp chống đánh\r\n\r\n◾️ Két sắt có 2 bản lề ngoài giúp tăng không gian sử dung ở bên trong và có 5 chốt khoá đúc đặc an toàn ăn sâu vào trong lòng két khi đóng cánh cửa lại tạo thành một vòng tròn khép kén giúp két của bạn không bị nậy phá két ra một cách dễ dàng\r\n\r\n◾️ Két sắt khoá cơ K41 có kết cầu 02 ngăn, phía trên cùng của két sắt có 01 ngăn kéo có khóa riêng biệt. phía dưới là khoang đựng giấy tờ A4 thoải mái....vv\r\n\r\n◾️ Két sắt được sử dụng sơn vấn búa đảm bảo bền mầu theo thời gian mà không bị bong hay chóc sơn\r\n\r\n◾️ Két sắt có khả năng chống cháy 1670oC trong vòng 120P\r\nSản phẩm Két sắt nhỏ chống cháy bao gồm:\r\n\r\n01 phiếu bảo hành 05 năm\r\n\r\n03 chìa khóa tổng\r\n\r\n02 chìa khóa ngăn kéo\r\n\r\n01 phiếu mã số và hướng dẫn sử dụng'),
(34, 'other', '../images/ongnhom.jpg', 'Ống nhòm', 700000, 0, '', 'Được chế tạo bằng vật liệu ABS, những chiếc ống nhòm này có khả năng chống va đập và tay cầm chống trượt, đảm bảo tuổi thọ và độ tin cậy trong những chuyến phiêu lưu ngoài trời. Ít bị hư hỏng, chúng lý tưởng để sử dụng chắc chắn trong các môi trường khác nhau.\r\n\r\nVới quang học nhiều lớp phủ phi cầu, những chiếc ống nhòm này mang lại màu sắc hình ảnh sống động và chính xác với độ sáng nhất quán trong mọi điều kiện ánh sáng.\r\n\r\nCấu hình 8x21 cung cấp khả năng phóng đại tiên tiến cho trường nhìn rõ ràng hơn và xa hơn, trong khi thị kính 12mm và vật kính 21mm cung cấp góc nhìn rộng để chụp phong cảnh chi tiết một cách dễ dàng.\r\n\r\nĐược trang bị núm lấy nét rộng, những chiếc ống nhòm này giúp lấy nét dễ dàng cho người dùng ở mọi lứa tuổi. Bánh xe điều chỉnh trung tâm cho phép tùy chỉnh chính xác, cho phép tinh chỉnh nhanh chóng và dễ dàng để đạt được độ rõ ràng và sắc nét tối ưu trong quan sát của bạn.\r\n\r\nĐược thiết kế để linh hoạt, những chiếc ống nhòm này thích hợp cho nhiều hoạt động như hòa nhạc, sự kiện thể thao, ngắm chim, đi bộ đường dài, du lịch, v.v. Cho dù bạn cần khả năng quan sát chính xác để theo đuổi giải trí hay ngoài trời, những chiếc ống nhòm này cung cấp hiệu suất đáng tin cậy trong nhiều tình huống khác nhau.\r\n\r\nMô tả:\r\n\r\nNhững chiếc ống nhòm đa năng này hoàn hảo cho các hoạt động ngoài trời, mang lại kết cấu bền và chống va đập. Chất liệu ABS và tay cầm chống trượt đảm bảo độ bám chắc chắn, lý tưởng cho các tình huống khác nhau.\r\n\r\nQuang học nhiều lớp phủ phi cầu mang lại màu sắc sống động và chính xác, trong khi hệ thống quang học đảm bảo độ sáng trong mọi điều kiện ánh sáng. Với độ phóng đại 8x và trường nhìn rộng, những chiếc ống nhòm này cung cấp hình ảnh rõ ràng và xa.\r\n\r\nNúm lấy nét rộng và bánh xe điều chỉnh trung tâm cho phép lấy nét dễ dàng và chính xác, phù hợp cho cả người lớn và trẻ em. Cho dù bạn đang ngắm chim, đi bộ đường dài, du lịch hay tham dự các sự kiện, những chiếc ống nhòm này mang lại hiệu suất vượt trội và dễ sử dụng.\r\n\r\n\r\n\r\nTên khoản mục: Ống nhòm\r\n\r\nChất liệu: ABS\r\n\r\nLớp phủ: Lớp phủ nhiều lớp FMC\r\n\r\nĐường kính thị kính: 12mm\r\n\r\nĐường kính vật kính: 22mm\r\n\r\nKhoảng cách học sinh thoát: 10mm\r\n\r\nĐường kính học sinh thoát: 45,8 Mm\r\n\r\nTrường nhìn: 5,6 độ\r\n\r\nTrường nhìn: 131 mét / 1000 mét\r\n\r\nCác tính năng: Di động, Nhẹ, Dễ sử dụng\r\n\r\nChi tiết kích thước:\r\n\r\n12cm x 9cm / 4,72 \"x 3,54\" (Xấp xỉ)\r\n\r\nGhi chú:\r\n\r\nDo sự khác biệt về cài đặt ánh sáng và màn hình, màu sắc của mặt hàng có thể hơi khác so với hình ảnh.\r\n\r\nVui lòng cho phép sự khác biệt kích thước nhỏ do đo lường thủ công khác nhau.\r\n\r\nGói bao gồm:\r\n\r\n1 x ống nhòm\r\n\r\n1 x Vải lau\r\n\r\n1 x Túi lưu trữ\r\n\r\n'),
(35, 'other', '../images/maychieu.jfif', 'Máy chiếu mini', 2200000, 2400000, 'hanggiamgia', '[Máy chiếu thông minh nhỏ gọn và mạnh nhất phân khúc giá rẻ] Máy chiếu Android Yaber L1 có kích thước vô cùng nhỏ gọn và chỉ nặng chưa tới 1kg. Có thiết kế tinh tế cao cấp cùng với các công nghệ tiên tiến mới nhất, Yaber L1 là máy chiếu thông minh di động đúng nghĩa.\r\n\r\n\r\n\r\n[Tích hợp hệ điều hành Android & Âm thanh nổi Hi-Fi] Máy chiếu mini Yaber L1 là máy chiếu giải trí đa năng được tích hợp hệ điều hành Android. Bạn có thể thưởng thức các chương trình giải trí bất tận với Youtube, Netflix, truyền hình, TV, các kho phim bom tấn được tích hợp sẵn trong máy. Yaber L1 còn được trang bị loa kép 5W chuẩn Dolby với hệ thống âm thanh SRS mang đến trải nghiệm tuyệt vời mà không cần đến loa ngoài.\r\n\r\n\r\n\r\n[Độ phân giải gốc HD720P hỗ trợ Full-HD và 4K đầu vào độ sáng cao] Máy chiếu Yaber L1 Android có độ phân giải gốc HD720p hỗ trợ FullHD và 4K, độ sáng 5500L, độ tương phản cao 8000:1, sáng hơn và rõ hơn 60% so với các máy chiếu tương đương khác. Với nhiều cổng kết nối: HDMI, USB, AV, Audio out. Máy chiếu L1 đa năng tương thích với TV box, Fire Stick TV, Chromecast, Laptop, Tablet, Loa ngoài, iPad, iPhone, Android smartphone.\r\n\r\n[Tích hợp hàng loạt các công nghệ tiên tiến] Máy chiếu thông minh Yaber L1 có khả năng lấy nét điện tử chuẩn xác, hệ thống điều chỉnh vuông hình tự động dọc khung hình và bộ xử lý trung tâm Basic Smart Engine hoạt động cực nhanh và khả năng tái tại mầu sắc sống động rực rỡ. Yaber L1 cũng được áp dụng công nghệ SmartEco mới nhất để giảm thiểu mức tiêu thụ điện năng, tăng độ sáng và kéo dài tuổi thọ của bóng chiếu.'),
(36, 'other', '../images/maydo-huyet-ap.png', 'Máy đo huyết áp', 900000, 0, '', 'Công nghệ IntelliSense™: Đo huyết áp thuận tiện gấp nhiều lần​\r\n\r\nTrải nghiệm cảm giác thoải mái vượt trội với vòng bít được trang bị Công nghệ IntelliSense của chúng tôi. Tận hưởng cảm giác bơm khí hoàn toàn tự động, phù hợp nhu cầu, mang lại trải nghiệm đo liền mạch. Việc xả khí sau khi đo cũng rất nhẹ nhàng, hiệu quả và đem lại sự thoải mái mỗi lần sử dụng.​\r\n\r\n​\r\n\r\nPhát hiện sớm bệnh tăng huyết áp​\r\n\r\nĐèn cảnh báo tăng huyết áp sẽ nhấp nháy nếu huyết áp tâm thu hoặc tâm trương của bạn nằm ngoài phạm vi tiêu chuẩn giúp bạn xác định sớm các vấn đề sức khỏe tiềm ẩn và thực hiện điều chỉnh lối sống thích hợp với sự tư vấn của bác sỹ.​\r\n\r\n​\r\n\r\nCá nhân hóa các thông tin và dữ liệu theo dõi​\r\n\r\nThiết bị tích hợp liền mạch với ứng dụng OMRON connect, cho phép bạn theo dõi chỉ số huyết áp, giám sát xu hướng và nhận thông tin chi tiết đã được cá nhân hóa. Chia sẻ các dữ liệu này với bác sỹ sẽ giúp họ hiểu rõ hơn về trạng thái tim mạch của bạn và từ đó giúp bạn thực hiện các biện pháp điều chỉnh lối sống hợp lý nhằm hỗ trợ sức khỏe tim mạch của bạn.​\r\n\r\n​\r\n\r\nCác tính năng chính của OMRON HEM-7140T:​\r\n\r\nĐược chứng nhận lâm sàng cho kết quả đo huyết áp chính xác, đáng tin cậy​\r\n\r\nĐèn báo tăng huyết áp sẽ cảnh báo khi huyết áp của bạn vượt ra khỏi phạm vi bình thường.​\r\n\r\nTích hợp liền mạch với điện thoại thông minh qua Bluetooth bằng ứng dụng OMRON connect để theo dõi và chia sẻ dữ liệu ​\r\n\r\nCông nghệ IntelliSense™ cho kết quả nhanh chóng và thuận tiện mọi lúc​\r\n\r\nĐược trang bị tính năng chỉ dẫn quấn vòng bít để mang lại kết quả đo chuẩn xác. ​\r\n\r\nThiết bị được bảo hành lên đến 5 năm (không bao gồm vòng bít và pin)​\r\n\r\nĐặc tính kỹ thuật:\r\n\r\nĐèn hướng dẫn quấn vòng bít\r\n\r\nChỉ báo tăng huyết áp\r\n\r\nCông nghệ IntelliSense\r\n\r\nLưu trữ 14 dữ liệu đo trong bộ nhớ\r\n\r\nỨng dụng OMRON connect giúp bạn dễ dàng lưu, theo dõi và quản lý dữ liệu sức khỏe của mình ở mọi nơi.\r\n\r\nNhật ký huyết áp được thiết kế đặc biệt để quản lý sức khỏe huyết áp của bạn'),
(37, 'furniture', '../images/giuong-ngu.jpg', 'Giường ngủ', 7000000, 0, '', 'Giường ngủ gỗ tự nhiên bền đẹp nhiều kích thước dài 2m\r\n\r\nTHÔNG SỐ SẢN PHẨM:\r\n\r\n- Đầu giường cao 75cm, Đuôi giường cao 30cm, khoảng hở nan 2cm. Giường có kích thước phủ bì lớn hơn 10cm ( thêm phần vai giường)\r\n\r\n👉 Vật liêu: Gỗ Thông Tự Nhiên Đã Sấy Khô 100%.\r\n\r\n👉 Xuất xứ: Xưởng Mộc HT tại việt nam với thợ có tay nghề nhiều năm làm sản phẩm theo thiết kế \r\n\r\n👉 Sản xuất: Xưởng Nội Thất HT\r\n\r\n👉 Đăc điểm: Sản phẩm dễ dàng tháo rời khi cần chuyển nhà chuyển phòng\r\n\r\nCác cạnh thanh gỗ được bo tròn R3mm chống dằm,bề mặt nan được chà nhám kỹ trước .'),
(38, 'furniture', '../images/den-trang-tri.jpg', 'Đèn trang trí', 800000, 0, '', '🌺 MÔ TẢ SẢN PHẨM\r\n\r\n▶️ Đèn Led dẫn lối trang trí thích hợp cho tiệc cưới trong nhà và ngoài trời nhằm tăng thêm sự lãng mạn, lung linh cho buổi tiệc. Ngoài ra đèn còn có thể sử dụng cho các bữa tiệc sinh nhật, sự kiện, trang trí sân vườn, nhà hàng, quán cà phê\r\n\r\n▶️ Các sản phẩm đèn led của shop với kiểu dáng và quy cách được ưa chuộng tại các thị trường châu Âu và Mỹ với nhiều ưu điểm vượt trội so với các sản phẩm dây đèn nháy thông dụng\r\n\r\n▶️ Sử dụng bóng LED có tuổi thọ cao, tiết kiệm điện. Có thể điều chỉnh chế độ nhấp nháy và không nhấp nháy tùy nhu cầu\r\n\r\n▶️ Ánh sáng vàng ấm lãng mạn, tinh tế\r\n\r\n▶️ Vỏ bóng đèn led được làm bằng nhựa nhiều góc cạnh tạo nên điểm sáng lớn khi trang trí trên cây, tiết kiệm số bóng đèn phải sử dụng so với các loại đèn nháy kim thông thường\r\n\r\n🍀 THÔNG SỐ KỸ THUẬT\r\n\r\n- Nguồn vào: 220V, tụ điện nhôm chống nước \r\n\r\n- Đèn: LED Vàng\r\n\r\n- Chất liệu: Nút nhựa, dây kẽm đồng, thanh nhôm\r\n\r\n- Chiều cao: Thân cắm 40cm, dây led kẽm 30cm -> tổng ~70cm\r\n\r\n- Chiều dài: Tổng 7m, mỗi bông cách nhau 1,35m, dồm 90 bóng LED bông\r\n\r\n- Chế độ điều khiển: chớp nhấp nháy tự động hoặc không chớp, 8 chế độ\r\n\r\n- Môi trường hoạt động: Trong nhà, ngoài trời. Các bộ không kết nối được với nhau\r\n\r\n'),
(39, 'furniture', '../images/ban-an.webp', 'Bàn ăn', 4500000, 5000000, 'hanggiamgia', 'THÔNG TIN MÔ TẢ SẢN PHẨM NGẮN\r\n\r\n* Chất liệu: mặt gỗ MDF phủ melamin chống nước, chân bàn gỗ sồi tự nhiên\r\n\r\n* Màu sắc: có 2 màu sắc: Mặt trắng và mặt vân gỗ\r\n\r\n* Kích thước: 80x80x75 (dài x rộng x cao) và 120x60x75 (dài x rộng x cao)\r\n\r\n* Cấu tạo: Lắp ráp\r\n\r\n* Công năng: Làm bàn ăn, bàn làm việc, bàn trà,...\r\nLƯU Ý\r\n\r\n- Khi bạn nhận được đơn đặt hàng, nếu sản phẩm bị hỏng hoặc không hài lòng với nó, vui lòng liên hệ với chúng tôi, xin vui lòng không để lại phản hồi tiêu cực, chúng tôi sẽ cố gắng hết sức để giải quyết nó cho bạn đúng cách, để bạn hài lòng!\r\n\r\n- Hình ảnh shop đăng là hình ảnh Render, màu sản phẩm có thể có một chút khác biệt so với sản phẩm thực tế\r\n\r\n- Xin vui lòng cho phép sai số  với thực tế 1-2 cm do đo lường thủ công'),
(40, 'furniture', '../images/ghe-don.jpg', 'Ghế đơn', 1000000, 0, '', '- Tên sản phẩm: Ghế đôn mini đệm hình hoa nhiều màu cute decor trang trí, siêu chắc chắn CHỒY Xì To\r\n\r\n- Chất liệu: Gỗ, nỉ, PP, Nhung bông\r\n\r\n- Màu sắc: nhiều màu\r\n\r\n- Kích thước: 18x34CM\r\n\r\n- Trọng lượng: 700gr\r\n\r\nQUY CÁCH ĐÓNG GÓI:\r\n\r\n- 1x  sản phẩm\r\nĐẶC ĐIỂM NỔI BẬT:\r\n\r\n- Thiết kế dáng chân 4 chân giảm âm, rất chắc chắn và không gây tiếng ồn, làm bằng gỗ thông \r\n\r\n- Hình dáng bông hoa kiểu dáng hiện đại, dễ dàng decor'),
(41, 'furniture', '../images/ke-trang-tri.jpg', 'Kệ trang trí', 2200000, 0, '', '🔆Lắp ráp🔆\r\n\r\n+ Bộ sản phẩm được đóng gói dạng rời gồm các tấm gỗ được dán nhãn và đánh số kèm theo sơ đồ lắp ráp, đầy đủ phụ kiện. \r\n\r\n+ Nếu có lỗi/hư hỏng do sản xuất hoặc do lỗi vận chuyển. SIB Decor cam kết gửi thay thế phần bị hỏng ngay khi nhận được yêu cầu.\r\n— — — — — — — — —\r\n\r\n🔅Thông số🔅\r\n\r\n- Chất liệu tốt: gỗ MDF lõi xanh chống ẩm, độ dày 17mm, bề mặt phủ melamine đồng màu, căng phẳng có khả năng kháng nước, hạn chế trầy xước, lau sạch dễ dàng.\r\n\r\n- Kích thước: 120 x 30 x 16,7cm (Dài x Cao x Rộng).\r\n\r\n- Màu sắc: Trắng;  Walnut + Trắng.\r\n\r\n- Ứng dụng: \r\n\r\n+ Để sách, chậu cây, khung ảnh, vật dụng trang trí. \r\n\r\n+ Phù hợp với nhiều không gian: Phòng khách, phòng ngủ, văn phòng làm việc.\r\n\r\n- Bộ sản phẩm gồm: Kệ gỗ, phụ kiện, hướng dẫn lắp đặt, tặng kèm nivo mini (thước thủy để lấy cân bằng).\r\n\r\n- Thương hiệu: SIB Decor.\r\n\r\n- Sản phẩm được sản xuất tại: Công ty TNHH Trang Trí Nội Thất Đơn Giản Là Đẹp\r\n\r\n- Xuất xứ: Việt Nam\r\n\r\n\r\n\r\n— — — — — — — — —\r\n\r\n🔅Điểm nổi bật🔅\r\n\r\n- Dễ dàng lắp ráp: Kệ gỗ được gia công bằng CNC tự động, các vị trí liên kết đều có độ chuẩn xác cao, phụ kiện hiện đại nên chúng mình nghĩ nó sẽ không làm khó được bạn đâu.\r\n\r\n*Các hướng dẫn gửi kèm bằng hình ảnh dễ hiểu, có tặng kèm nivo cân bằng để bạn lắp kệ không bị nghiêng, và luôn có đội ngũ kỹ thuật hỗ trợ bạn khi nhận được yêu cầu.\r\n\r\n- Đẹp: Sản phẩm thiết kế tối ưu về công năng sử dụng, khả năng chịu lực và luôn được xử lý đẹp Trước – Sau – Trên – Dưới. Các vị trí liên kết đều được cung cấp miếng dán decal đồng màu để che lại rất thẩm mỹ.'),
(42, 'furniture', '../images/tu-giay.webp', 'Tủ giày', 1500000, 2000000, 'hanggiamgia', '- Ưu điểm của tủ đựng giày:\r\n\r\n1. Lắp đặt nhanh chóng và tiện lợi, thiết kế gấp có thể di chuyển tủ một cách dễ dàng\r\n\r\n2. Khả năng chịu tải tốt, mỗi hộp lên đến 20kg\r\n\r\n3. Bề dày khung xương tủ lên đến 30mm\r\n\r\n4. Ốc vít ở các khớp nối cố định vững chắc nên sản phẩm không bị rung lắc\r\n\r\n5. Thiết kế cửa hút từ tính mạnh\r\n\r\n6. Dung lượng ô chứa đồ lớn\r\n\r\n7. Tủ thiết kế liền mạch và hoàn toàn kín, không gian rộng\r\n\r\n8. Thanh phơi quần áo hợp kim nhôm chắc chắn\r\n\r\n9. Linh kiện có thể tháo rới dễ dàng trong việc thay thế linh kiện và bảo hành.\r\n\r\n10. Chiều cao 1 ô là 16cm, ô boot 32cm, chiều sâu 34cm\r\n\r\n*Sản phẩm được làm bằng chất liệu chính là nhựa ABS siêu cứng, siêu dai, rất bền, chống thấm nước và cách điện, không biến dạng, trong môi trường tự nhiên, không độc hại.\r\n*Kích thước : 85x34x172\r\n\r\n*Trọng lượng: 18.38kg\r\n\r\n*Chất liệu: nhựa ABS, PP, PET, Magnet'),
(43, 'furniture', '../images/ban-lam-viec.jpg', 'Bàn làm việc', 3000000, 0, '', '-	Tên sản phẩm: Bàn làm việc chữ L thông minh kèm kệ sách thương hiệu IGA\r\n\r\n-	Màu sắc: Trắng - vân gỗ\r\n\r\n-	Chất liệu: Gỗ mdf phủ melamin chống xước chống nước\r\n\r\n-	Kích thước: 120/140x60x75cm\r\n\r\n-	Công năng: Là mẫu bàn làm việc, bàn gaming hiện đại với thiết kế chữ L tối ưu không gian phòng hiệu quả\r\n\r\n-	Thương hiệu: IGA\r\n\r\n'),
(44, 'furniture', '../images/tham-trai-san.jpg', 'Thảm trải sàn', 1200000, 0, '', 'Chất liệu: Sợi polyester (polyester)\r\n\r\nHình dạng: Hình vuông\r\n\r\nLoại làm sạch: Giặt\r\n\r\nMàu sắc: Màu be + xám, xám, Xám nhạt, Nâu nhạt, Màu be, chữ cái, Xanh lam​\r\n\r\nKích thước: 80 × 120cm, 160 × 230cm, 200 × 300cm\r\n\r\nkhông gian: Phòng khách, phòng ngủ'),
(45, 'furniture', '../images/tu-quan-ao.jpg', 'Tủ quần áo', 6000000, 6500000, 'hanggiamgia', '☑️ THÔNG TIN SẢN PHẨM\r\n\r\nLựa chọn phong cách thiết kế tối giản, là phong cách thể hiện sự đa dạng nghệ thuật thị giác, tủ quần áo IGA mang đến một diện mạo \" tinh tế & thời thượng\" hướng đến giá trị của không gian là sự thoáng đãng. Với sản phẩm này, không gian ngôi nhà bạn trở lên tối giản, giảm bớt sự lộn xộn và đơn giản hóa nội thất. Cho bạn cảm giác thư thái. thoải mái khi về nhà.\r\n\r\n\r\n\r\n Màu sắc: Phối trắng - vân gỗ sáng\r\n\r\n Chất liệu: Sản phẩm làm bằng gỗ MDF phủ Melamine chống trầy xước dễ dàng vệ sinh, vô cùng chắc chắn và bền bỉ theo  Công năng: Lưu trữ, bảo quản đồ đạc, Decor trang trí nhà cửa\r\n\r\n🆘 LƯU Ý \r\n\r\n- Nếu bạn nhận hàng chất lượng kém không giống mô tả shop hoàn tiền ngay 100% cho bạn.\r\n\r\n- Nếu sản phẩm bị lỗi do vận chuyển shop đổi sản phẩm mới miễn phí cho bạn.');

